// Composant de Chat avec vrai Socket.IO
function ChatRoom({ room, onClose }) {
  const { user } = useAuth();
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [socket, setSocket] = useState(null);
  const [isConnected, setIsConnected] = useState(false);

  useEffect(() => {
    // Initialiser la connexion Socket.IO
    const newSocket = io('http://localhost:5000');

    newSocket.on('connect', () => {
      console.log('ðŸ”Œ ConnectÃ© au serveur Socket.IO');
      setIsConnected(true);

      // Rejoindre le room
      newSocket.emit('join_room', room._id);
      console.log(`ðŸšª Rejoint le salon: ${room.name}`);
    });

    newSocket.on('disconnect', () => {
      console.log('ðŸ”Œ DÃ©connectÃ© du serveur Socket.IO');
      setIsConnected(false);
    });

    // Ã‰couter les nouveaux messages
    newSocket.on('new_message', (message) => {
      console.log('ðŸ’¬ Nouveau message reÃ§u:', message);
      setMessages(prev => [...prev, message]);
    });

    // Charger l'historique des messages
    loadMessageHistory(room._id);

    setSocket(newSocket);

    // Nettoyer Ã  la dÃ©connexion
    return () => {
      newSocket.emit('leave_room', room._id);
      newSocket.disconnect();
    };
  }, [room]);

  const loadMessageHistory = async (roomId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000/api/rooms/${roomId}/messages`, {
        headers: { 'Authorization': `Bearer ${token}` }
      });

      if (response.ok) {
        const data = await response.json();
        if (data.success) {
          setMessages(data.data.messages || []);
        }
      }
    } catch (error) {
      console.log('Aucun historique de messages ou erreur:', error);
      // Simulation d'historique si l'API n'existe pas encore
      setMessages([
        {
          _id: 1,
          content: `Bienvenue dans le salon ${room.name} ! ðŸŽ‰`,
          user: { _id: 'system', username: 'SystÃ¨me' },
          createdAt: new Date(),
          isSystem: true
        },
        {
          _id: 2,
          content: 'Commencez Ã  discuter avec les autres membres !',
          user: { _id: 'system', username: 'SystÃ¨me' },
          createdAt: new Date(),
          isSystem: true
        }
      ]);
    }
  };

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (!newMessage.trim() || !socket || !isConnected) return;

    // Envoyer le message via Socket.IO
    socket.emit('send_message', {
      roomId: room._id,
      content: newMessage.trim()
    });

    // Ajouter le message localement immÃ©diatement (optimistic update)
    const tempMessage = {
      _id: Date.now(), // ID temporaire
      content: newMessage.trim(),
      user: user,
      createdAt: new Date(),
      isTemporary: true
    };

    setMessages(prev => [...prev, tempMessage]);
    setNewMessage('');
  };

  const formatTime = (date) => {
    if (typeof date === 'string') {
      date = new Date(date);
    }
    return date.toLocaleTimeString('fr-FR', { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  const formatDate = (date) => {
    if (typeof date === 'string') {
      date = new Date(date);
    }
    return date.toLocaleDateString('fr-FR');
  };

  // Grouper les messages par date
  const groupMessagesByDate = () => {
    const groups = {};
    messages.forEach(message => {
      const date = formatDate(message.createdAt);
      if (!groups[date]) {
        groups[date] = [];
      }
      groups[date].push(message);
    });
    return groups;
  };

  const messageGroups = groupMessagesByDate();

  return (
    <div style={{
      display: 'flex',
      flexDirection: 'column',
      height: '600px',
      border: '1px solid #e9ecef',
      borderRadius: '8px',
      background: 'white',
      marginLeft: '20px',
      flex: 2,
      minWidth: '500px'
    }}>
      {/* En-tÃªte du chat */}
      <div style={{
        padding: '15px 20px',
        borderBottom: '1px solid #e9ecef',
        background: '#f8f9fa',
        borderRadius: '8px 8px 0 0',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center'
      }}>
        <div>
          <h3 style={{ margin: 0, color: '#333' }}>#{room.name}</h3>
          <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
            <small style={{ color: '#6c757d' }}>
              {room.members.length} membre(s)
            </small>
            <div style={{
              width: '8px',
              height: '8px',
              borderRadius: '50%',
              background: isConnected ? '#28a745' : '#dc3545'
            }} />
            <small style={{ color: isConnected ? '#28a745' : '#dc3545' }}>
              {isConnected ? 'En ligne' : 'Hors ligne'}
            </small>
          </div>
        </div>
        <button 
          onClick={onClose}
          style={{
            background: '#6c757d',
            color: 'white',
            border: 'none',
            padding: '8px 12px',
            borderRadius: '5px',
            cursor: 'pointer'
          }}
        >
          âœ• Fermer
        </button>
      </div>

      {/* Zone des messages */}
      <div style={{
        flex: 1,
        overflowY: 'auto',
        padding: '20px',
        background: '#fafafa'
      }}>
        {messages.length === 0 ? (
          <div style={{
            textAlign: 'center',
            color: '#666',
            fontStyle: 'italic',
            marginTop: '50px'
          }}>
            <div style={{ fontSize: '48px', marginBottom: '10px' }}>ðŸ’¬</div>
            <div>Aucun message dans ce salon.</div>
            <div>Soyez le premier Ã  envoyer un message !</div>
          </div>
        ) : (
          Object.entries(messageGroups).map(([date, dayMessages]) => (
            <div key={date}>
              {/* SÃ©parateur de date */}
              <div style={{
                textAlign: 'center',
                margin: '20px 0',
                position: 'relative'
              }}>
                <div style={{
                  display: 'inline-block',
                  background: '#e9ecef',
                  color: '#6c757d',
                  padding: '5px 15px',
                  borderRadius: '15px',
                  fontSize: '12px',
                  fontWeight: 'bold'
                }}>
                  {date}
                </div>
              </div>

              {/* Messages du jour */}
              {dayMessages.map((message) => (
                <div
                  key={message._id}
                  style={{
                    marginBottom: '15px',
                    padding: message.isSystem ? '10px 15px' : '12px 16px',
                    borderRadius: message.isSystem ? '5px' : '12px',
                    background: message.isSystem ? '#e9ecef' : 
                               message.user._id === user._id ? '#007bff' : 'white',
                    color: message.isSystem ? '#6c757d' : 
                          message.user._id === user._id ? 'white' : '#333',
                    border: message.isSystem ? 'none' : 
                           message.user._id === user._id ? 'none' : '1px solid #e9ecef',
                    maxWidth: message.isSystem ? '100%' : '75%',
                    alignSelf: message.user._id === user._id ? 'flex-end' : 'flex-start',
                    marginLeft: message.user._id === user._id ? 'auto' : '0',
                    marginRight: message.user._id === user._id ? '0' : 'auto',
                    opacity: message.isTemporary ? 0.7 : 1,
                    fontStyle: message.isSystem ? 'italic' : 'normal'
                  }}
                >
                  {!message.isSystem && (
                    <div style={{
                      display: 'flex',
                      justifyContent: 'space-between',
                      alignItems: 'flex-start',
                      marginBottom: '5px'
                    }}>
                      <strong style={{
                        fontSize: '14px',
                        color: message.user._id === user._id ? 'white' : '#007bff'
                      }}>
                        {message.user._id === user._id ? 'Vous' : message.user.username}
                      </strong>
                      <small style={{
                        opacity: 0.8,
                        fontSize: '11px',
                        marginLeft: '10px'
                      }}>
                        {formatTime(message.createdAt)}
                        {message.isTemporary && ' (envoi...)'}
                      </small>
                    </div>
                  )}
                  <div style={{ 
                    lineHeight: '1.4',
                    fontSize: message.isSystem ? '13px' : '14px'
                  }}>
                    {message.content}
                  </div>
                </div>
              ))}
            </div>
          ))
        )}
      </div>

      {/* Zone de saisie */}
      <form onSubmit={handleSendMessage} style={{
        padding: '20px',
        borderTop: '1px solid #e9ecef',
        background: 'white',
        borderRadius: '0 0 8px 8px'
      }}>
        <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
          <input
            type="text"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder={isConnected ? "Tapez votre message..." : "Connexion en cours..."}
            disabled={!isConnected}
            style={{
              flex: 1,
              padding: '12px 15px',
              border: `1px solid ${isConnected ? '#ddd' : '#eee'}`,
              borderRadius: '20px',
              outline: 'none',
              fontSize: '14px',
              background: isConnected ? 'white' : '#f8f9fa'
            }}
            onKeyPress={(e) => {
              if (e.key === 'Enter' && !e.shiftKey) {
                e.preventDefault();
                handleSendMessage(e);
              }
            }}
          />
          <button
            type="submit"
            disabled={!newMessage.trim() || !isConnected}
            style={{
              padding: '12px 20px',
              background: (newMessage.trim() && isConnected) ? '#007bff' : '#ccc',
              color: 'white',
              border: 'none',
              borderRadius: '20px',
              cursor: (newMessage.trim() && isConnected) ? 'pointer' : 'not-allowed',
              fontSize: '14px'
            }}
          >
            {isConnected ? 'ðŸ“¤' : 'âŒ›'}
          </button>
        </div>
        {!isConnected && (
          <div style={{
            textAlign: 'center',
            color: '#dc3545',
            fontSize: '12px',
            marginTop: '8px'
          }}>
            Tentative de connexion au serveur...
          </div>
        )}
      </form>
    </div>
  );
}
