import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import './Auth.css';

const Login = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: ''
  });
  const [formErrors, setFormErrors] = useState({});
  
  const { login, register, loading, error, clearError } = useAuth();

  // Effacer les erreurs quand on change de mode
  useEffect(() => {
    clearError();
    setFormErrors({});
  }, [isLogin, clearError]);

  /**
   * Validation du formulaire
   */
  const validateForm = () => {
    const errors = {};

    if (!isLogin && !formData.username.trim()) {
      errors.username = 'Le nom d\'utilisateur est requis';
    }

    if (!formData.email.trim()) {
      errors.email = 'L\'email est requis';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      errors.email = 'Format d\'email invalide';
    }

    if (!formData.password) {
      errors.password = 'Le mot de passe est requis';
    } else if (formData.password.length < 6) {
      errors.password = 'Le mot de passe doit contenir au moins 6 caractères';
    }

    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  /**
   * Gestion de la soumission
   */
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }

    const result = isLogin 
      ? await login(formData)
      : await register(formData);

    if (result.success) {
      // Redirection gérée par le contexte
      console.log('Authentification réussie!');
    }
  };

  /**
   * Gestion du changement des champs
   */
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Effacer l'erreur du champ quand l'utilisateur tape
    if (formErrors[name]) {
      setFormErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <div className="auth-header">
          <h1>ChatMe</h1>
          <p>{isLogin ? 'Connectez-vous à votre compte' : 'Créez votre compte'}</p>
        </div>

        {/* Message d'erreur global */}
        {error && (
          <div className="error-message global-error">
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit} className="auth-form">
          {/* Champ username seulement pour l'inscription */}
          {!isLogin && (
            <div className="form-group">
              <label htmlFor="username">Nom d'utilisateur</label>
              <input
                type="text"
                id="username"
                name="username"
                value={formData.username}
                onChange={handleChange}
                placeholder="Votre nom d'utilisateur"
                className={formErrors.username ? 'error' : ''}
              />
              {formErrors.username && (
                <span className="field-error">{formErrors.username}</span>
              )}
            </div>
          )}
          
          {/* Champ email */}
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="votre@email.com"
              className={formErrors.email ? 'error' : ''}
            />
            {formErrors.email && (
              <span className="field-error">{formErrors.email}</span>
            )}
          </div>

          {/* Champ mot de passe */}
          <div className="form-group">
            <label htmlFor="password">Mot de passe</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="Votre mot de passe"
              className={formErrors.password ? 'error' : ''}
            />
            {formErrors.password && (
              <span className="field-error">{formErrors.password}</span>
            )}
          </div>

          {/* Bouton de soumission */}
          <button 
            type="submit" 
            className="auth-button"
            disabled={loading}
          >
            {loading ? 'Chargement...' : (isLogin ? 'Se connecter' : 'Créer un compte')}
          </button>
        </form>

        {/* Lien pour basculer entre connexion et inscription */}
        <div className="auth-footer">
          <p>
            {isLogin ? "Pas de compte ? " : "Déjà un compte ? "}
            <button 
              type="button" 
              className="link-button"
              onClick={() => setIsLogin(!isLogin)}
              disabled={loading}
            >
              {isLogin ? 'Créer un compte' : 'Se connecter'}
            </button>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
