// Composant de Chat
function ChatRoom({ room, onClose }) {
  const { user } = useAuth();
  const { theme } = useTheme();
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [socket, setSocket] = useState(null);
  const [isConnected, setIsConnected] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState('connecting');

  useEffect(() => {
    console.log(' Initialisation Socket.IO pour le salon:', room.name);

    let newSocket;

    try {
      const token = localStorage.getItem('token');
      console.log(' Tentative de connexion Socket.IO...');

      newSocket = io('http://localhost:5000', {
        auth: {
          token: token || 'demo-token'
        },
        timeout: 5000,
        transports: ['websocket', 'polling']
      });

      newSocket.on('connect', () => {
        console.log(' ConnectÃ© au serveur Socket.IO');
        setIsConnected(true);
        setConnectionStatus('connected');
        newSocket.emit('join_room', room._id);
      });

      newSocket.on('disconnect', (reason) => {
        console.log(' DÃ©connectÃ© du serveur Socket.IO:', reason);
        setIsConnected(false);
        setConnectionStatus('disconnected');
      });

      newSocket.on('connect_error', (error) => {
        console.error(' Erreur connexion Socket.IO:', error.message);
        setIsConnected(false);
        setConnectionStatus('error');

        setTimeout(() => {
          if (!isConnected) {
            setConnectionStatus('local');
          }
        }, 3000);
      });

      newSocket.on('new_message', (message) => {
        console.log(' Nouveau message temps rÃ©el:', message);
        setMessages(prev => {
          const filtered = prev.filter(msg => !msg.isTemporary);
          return [...filtered, message];
        });
      });

      setSocket(newSocket);

    } catch (error) {
      console.error(' Erreur initialisation Socket.IO:', error);
      setConnectionStatus('local');
    }

    loadMessageHistory(room._id);

    return () => {
      if (newSocket) {
        newSocket.emit('leave_room', room._id);
        newSocket.disconnect();
      }
    };
  }, [room._id]);

  async function loadMessageHistory(roomId) {
    try {
      const demoMessages = [
        {
          _id: 1,
          content: `Bienvenue dans le salon ${room.name} ! `,
          user: { _id: 'system', username: 'SystÃ¨me' },
          createdAt: new Date(),
          isSystem: true
        },
        {
          _id: 2,
          content: connectionStatus === 'connected'
            ? 'Socket.IO connectÃ© - Temps rÃ©el activÃ© ! '
            : 'Mode local - Messages simulÃ©s ',
          user: { _id: 'system', username: 'SystÃ¨me' },
          createdAt: new Date(),
          isSystem: true
        }
      ];
      setMessages(demoMessages);
    } catch (error) {
      console.log(' Erreur chargement historique:', error);
    }
  }

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (!newMessage.trim()) return;

    const tempMessage = {
      _id: `temp-${Date.now()}`,
      content: newMessage.trim(),
      user: user,
      createdAt: new Date(),
      isTemporary: true
    };

    setMessages(prev => [...prev, tempMessage]);
    setNewMessage('');

    if (isConnected && socket) {
      socket.emit('send_message', {
        roomId: room._id,
        content: newMessage.trim()
      });
    } else {
      setTimeout(() => {
        const responseMessage = {
          _id: Date.now(),
          content: `[Mode Local] RÃ©ponse Ã : "${newMessage.trim()}"`,
          user: { _id: 'bot', username: 'Bot Local' },
          createdAt: new Date()
        };
        setMessages(prev => {
          const filtered = prev.filter(msg => msg._id !== tempMessage._id);
          return [...filtered, responseMessage];
        });
      }, 1000);
    }
  };

  const getStatusInfo = () => {
    switch(connectionStatus) {
      case 'connected':
        return { color: '#10b981', text: 'ðŸŸ¢ Temps RÃ©el Actif', message: 'Socket.IO ConnectÃ©' };
      case 'connecting':
        return { color: '#f59e0b', text: 'ðŸŸ¡ Connexion...', message: 'Tentative de connexion' };
      case 'error':
        return { color: '#ef4444', text: 'ðŸ”´ Hors Ligne', message: 'Erreur de connexion' };
      case 'local':
        return { color: '#6c757d', text: 'ðŸ”µ Mode Local', message: 'Messages simulÃ©s' };
      default:
        return { color: '#6c757d', text: 'âšª Inconnu', message: 'Statut indÃ©terminÃ©' };
    }
  };

  const statusInfo = getStatusInfo();

  const formatTime = (date) => {
    if (typeof date === 'string') date = new Date(date);
    return date.toLocaleTimeString('fr-FR', { hour: '2-digit', minute: '2-digit' });
  };

  const formatDate = (date) => {
    if (typeof date === 'string') date = new Date(date);
    return date.toLocaleDateString('fr-FR');
  };

  const groupMessagesByDate = () => {
    const groups = {};
    messages.forEach(message => {
      const date = formatDate(message.createdAt);
      if (!groups[date]) groups[date] = [];
      groups[date].push(message);
    });
    return groups;
  };

  const messageGroups = groupMessagesByDate();

  return (
    <div style={{
      display: 'flex',
      flexDirection: 'column',
      height: '600px',
      border: `1px solid ${theme === 'dark' ? 'var(--secondary-300)' : '#e2e8f0'}`,
      borderRadius: '12px',
      background: theme === 'dark' ? 'var(--secondary-100)' : '#f8fafc',
      marginLeft: '20px',
      flex: 2,
      minWidth: '500px',
      boxShadow: 'var(--shadow-md)',
      animation: 'slideInFromBottom 0.5s ease-out'
    }}>
      <div style={{
        padding: '18px 24px',
        borderBottom: `1px solid ${theme === 'dark' ? 'var(--secondary-300)' : '#e2e8f0'}`,
        background: 'linear-gradient(135deg, var(--primary-600) 0%, var(--primary-700) 100%)',
        color: 'white',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        position: 'relative',
        overflow: 'hidden'
      }}>
        <div style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          height: '2px',
          background: 'linear-gradient(90deg, transparent, rgba(255,255,255,0.6), transparent)',
          animation: 'buttonShine 3s infinite linear'
        }}></div>

        <div style={{ flex: 1 }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '12px', marginBottom: '6px' }}>
            <h3 style={{ margin: 0, fontSize: '1.25rem', fontWeight: '600' }}>#{room.name}</h3>
            <div style={{ 
              width: '10px', 
              height: '10px', 
              borderRadius: '50%', 
              background: statusInfo.color,
              animation: 'glow 2s infinite'
            }} />
          </div>
          <div style={{ display: 'flex', alignItems: 'center', gap: '15px', fontSize: '0.875rem', opacity: 0.9 }}>
            <span> {room.members?.length || 1} membre(s)</span>
            <span style={{ 
              background: 'rgba(255,255,255,0.2)', 
              padding: '2px 8px', 
              borderRadius: '12px', 
              fontWeight: '500',
              animation: 'fadeIn 0.5s ease-out'
            }}>
              {statusInfo.text}
            </span>
          </div>
        </div>

        <button 
          onClick={onClose}
          style={{
            background: 'rgba(255,255,255,0.2)',
            color: 'white',
            border: 'none',
            padding: '10px 14px',
            borderRadius: '8px',
            cursor: 'pointer',
            transition: 'all 0.3s ease',
            fontWeight: '500'
          }}
          className="hover-lift bounce-animation"
        >
          âœ• Fermer
        </button>
      </div>

      <div style={{ 
        flex: 1, 
        overflowY: 'auto', 
        padding: '20px', 
        background: theme === 'dark' ? 'var(--secondary-100)' : '#f8fafc' 
      }}>
        {messages.length === 0 ? (
          <div style={{ 
            textAlign: 'center', 
            color: theme === 'dark' ? 'var(--secondary-500)' : '#64748b', 
            marginTop: '80px',
            animation: 'fadeIn 0.6s ease-out'
          }}>
            <div style={{ 
              fontSize: '64px', 
              marginBottom: '20px', 
              opacity: 0.5,
              animation: 'float 3s ease-in-out infinite'
            }}>ðŸ’¬</div>
            <div style={{ fontSize: '18px', marginBottom: '8px', fontWeight: '500' }}>Aucun message dans ce salon</div>
            <div style={{ fontSize: '14px' }}>Soyez le premier Ã  envoyer un message !</div>
          </div>
        ) : (
          Object.entries(messageGroups).map(([date, dayMessages]) => (
            <div key={date}>
              <div style={{ textAlign: 'center', margin: '25px 0' }}>
                <div style={{
                  display: 'inline-block',
                  background: theme === 'dark' ? 'var(--secondary-300)' : '#e2e8f0',
                  color: theme === 'dark' ? 'var(--secondary-700)' : '#475569',
                  padding: '6px 16px',
                  borderRadius: '20px',
                  fontSize: '0.875rem',
                  fontWeight: '600',
                  animation: 'fadeIn 0.4s ease-out'
                }}>
                  {date}
                </div>
              </div>

              {dayMessages.map((message, index) => (
                <div
                  key={message._id}
                  style={{
                    marginBottom: '16px',
                    padding: message.isSystem ? '14px 18px' : '16px 20px',
