// Composant Modal de Profil
function ProfileModal({ isOpen, onClose }) {
  const { user, updateProfile } = useAuth();
  const { theme } = useTheme();
  const [formData, setFormData] = useState({
    username: user?.username || '',
    email: user?.email || ''
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  useEffect(() => {
    if (user && isOpen) {
      setFormData({
        username: user.username || '',
        email: user.email || ''
      });
    }
  }, [user, isOpen]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    e.stopPropagation();
    setLoading(true);
    setMessage('');

    try {
      const result = await updateProfile(formData.username, formData.email);

      setLoading(false);
      if (result.success) {
        setMessage('âœ… Profil mis Ã  jour avec succÃ¨s');
        setTimeout(() => {
          onClose();
        }, 2000);
      } else {
        setMessage(`âŒ ${result.message}`);
      }
    } catch (error) {
      setLoading(false);
      setMessage('âŒ Erreur lors de la mise Ã  jour du profil');
    }
  };

  const handleOverlayClick = (e) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  if (!isOpen) return null;

  return (
    <div 
      style={{
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        background: 'rgba(0,0,0,0.5)',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        zIndex: 1000
      }}
      onClick={handleOverlayClick}
    >
      <div 
        style={{
          background: theme === 'dark' ? 'var(--secondary-100)' : 'white',
          color: theme === 'dark' ? 'var(--secondary-900)' : '#1e293b',
          padding: '25px',
          borderRadius: '12px',
          width: '90%',
          maxWidth: '400px',
          boxShadow: 'var(--shadow-lg)',
          border: theme === 'dark' ? '1px solid var(--secondary-300)' : '1px solid #e2e8f0',
          animation: 'slideInFromBottom 0.4s cubic-bezier(0.68, -0.55, 0.265, 1.55)'
        }}
        onClick={(e) => e.stopPropagation()}
      >
        <h3 style={{ marginBottom: '20px', textAlign: 'center' }}>
          âœï¸ Modifier le profil
        </h3>

        {message && (
          <div style={{
            background: message.includes('âœ…') ? 
              (theme === 'dark' ? '#064e3b' : '#d1fae5') : 
              (theme === 'dark' ? '#7f1d1d' : '#fef2f2'),
            color: message.includes('âœ…') ? 
              (theme === 'dark' ? '#34d399' : '#065f46') : 
              (theme === 'dark' ? '#fca5a5' : '#dc2626'),
            padding: '12px',
            borderRadius: '8px',
            marginBottom: '15px',
            fontSize: '0.875rem',
            textAlign: 'center'
          }}>
            {message}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div style={{ marginBottom: '15px' }}>
            <label style={{ 
              display: 'block', 
              marginBottom: '5px', 
              fontWeight: '500',
              fontSize: '0.875rem'
            }}>
              Nom d'utilisateur
            </label>
            <input
              type="text"
