import React, { createContext, useState, useContext, useEffect, useCallback } from 'react';
import { authAPI } from '../services/api';

// CrÃ©ation du contexte d'authentification
const AuthContext = createContext();

/**
 * Hook personnalisÃ© pour utiliser le contexte d'authentification
 */
export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

/**
 * Provider du contexte d'authentification
 */
export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  /**
   * DÃ©connexion de l'utilisateur
   */
  const logout = useCallback(async () => {
    console.log(' DÃ©connexion en cours...');
    try {
      const token = localStorage.getItem('token');
      if (token) {
        await authAPI.logout();
      }
    } catch (error) {
      console.error(' Erreur API dÃ©connexion:', error);
    } finally {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      setUser(null);
      setError('');
      setLoading(false);
      console.log(' DÃ©connexion terminÃ©e');
    }
  }, []);

  /**
   * VÃ©rifie si l'utilisateur est authentifiÃ©
   */
  const checkAuth = useCallback(async () => {
    try {
      console.log('ðŸ” VÃ©rification de l\'authentification...');
      
      const token = localStorage.getItem('token');
      const savedUser = localStorage.getItem('user');

      console.log(' Token prÃ©sent:', !!token);
      console.log(' User sauvegardÃ©:', !!savedUser);

      if (!token) {
        console.log(' Aucun token - DÃ©connexion');
        setLoading(false);
        return;
      }

      try {
        console.log(' VÃ©rification du token...');
        const response = await authAPI.verify();
        
        if (response.data.success) {
          const userData = response.data.data.user;
          console.log(' Token valide - Utilisateur:', userData.username);
          setUser(userData);
        } else {
          console.log(' Token invalide - DÃ©connexion');
          logout();
        }
      } catch (error) {
        console.error(' Erreur vÃ©rification:', error.response?.data || error.message);
        
        // En cas d'erreur rÃ©seau, utiliser les donnÃ©es sauvegardÃ©es
        if (savedUser) {
          try {
            const userData = JSON.parse(savedUser);
            console.log(' Utilisation donnÃ©es sauvegardÃ©es:', userData.username);
            setUser(userData);
          } catch (parseError) {
            console.error(' Erreur parsing user data:', parseError);
            logout();
          }
        } else {
          logout();
        }
      }
    } catch (error) {
      console.error(' Erreur checkAuth:', error);
      logout();
    } finally {
      setLoading(false);
    }
  }, [logout]);

  // VÃ©rifier l'authentification au chargement
  useEffect(() => {
    checkAuth();
  }, [checkAuth]);

  /**
   * Connexion d'un utilisateur existant
   */
  const login = useCallback(async (credentials) => {
    try {
      setError('');
      setLoading(true);
      console.log(' Tentative de connexion...');

      const response = await authAPI.login(credentials);
      
      if (response.data.success) {
        const { token, user } = response.data.data;
        
        console.log(' Connexion rÃ©ussie - Token:', token ? 'PRÃ‰SENT' : 'ABSENT');
        console.log(' User reÃ§u:', user);
        
        //  CORRECTION : Sauvegarder AVANT de mettre Ã  jour l'Ã©tat
        localStorage.setItem('token', token);
        localStorage.setItem('user', JSON.stringify(user));
        
        // VÃ©rifier que c'est bien sauvegardÃ©
        console.log(' Token sauvegardÃ©:', localStorage.getItem('token') ? 'OK' : 'ERREUR');
        console.log(' User sauvegardÃ©:', localStorage.getItem('user') ? 'OK' : 'ERREUR');
        
        setUser(user);
        
        return { success: true, data: response.data };
      }
    } catch (error) {
      const errorMessage = error.response?.data?.message || 'Erreur lors de la connexion';
      setError(errorMessage);
      return { success: false, message: errorMessage };
    } finally {
      setLoading(false);
    }
  }, []);

  /**
   * Inscription d'un nouvel utilisateur
   */
  const register = useCallback(async (userData) => {
    try {
      setError('');
      setLoading(true);
      console.log('Tentative d\'inscription...');

      const response = await authAPI.register(userData);
      
      if (response.data.success) {
        const { token, user } = response.data.data;
        
        console.log('Inscription rÃ©ussie - Token:', token ? 'PRÃ‰SENT' : 'ABSENT');
        console.log(' User reÃ§u:', user);
        
        //  CORRECTION : Sauvegarder AVANT de mettre Ã  jour l'Ã©tat
        localStorage.setItem('token', token);
        localStorage.setItem('user', JSON.stringify(user));
        
        // VÃ©rifier que c'est bien sauvegardÃ©
        console.log('Token sauvegardÃ©:', localStorage.getItem('token') ? 'OK' : 'ERREUR');
        console.log(' User sauvegardÃ©:', localStorage.getItem('user') ? 'OK' : 'ERREUR');
        
        setUser(user);
        
        return { success: true, data: response.data };
      }
    } catch (error) {
      const errorMessage = error.response?.data?.message || 'Erreur lors de l\'inscription';
      setError(errorMessage);
      return { success: false, message: errorMessage };
    } finally {
      setLoading(false);
    }
  }, []);

  /**
   * Effacer les messages d'erreur
   */
  const clearError = useCallback(() => {
    setError('');
  }, []);

  const value = {
    user,
    loading,
    error,
    register,
    login,
    logout,
    clearError,
    checkAuth,
    isAuthenticated: !!user
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
