import axios from 'axios';

// Configuration de base de l'API
const API_URL = 'http://localhost:5000/api';

// Création de l'instance axios
const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Intercepteur pour ajouter le token aux requêtes
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Intercepteur pour gérer les erreurs de réponse
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      // Token expiré ou invalide
      localStorage.removeItem('token');
      window.location.href = '/';
    }
    return Promise.reject(error);
  }
);

// Services pour l'authentification
export const authAPI = {
  login: (email, password) => api.post('/auth/login', { email, password }),
  register: (username, email, password) => api.post('/auth/register', { username, email, password }),
  getProfile: () => api.get('/auth/profile'),
};

// Services pour les salons de discussion
export const chatRoomAPI = {
  create: (roomData) => api.post('/chatrooms', roomData),
  getAll: (params) => api.get('/chatrooms', { params }),
  getMyRooms: () => api.get('/chatrooms/my'),
  join: (roomId) => api.post(`/chatrooms/${roomId}/join`),
  leave: (roomId) => api.post(`/chatrooms/${roomId}/leave`),
};

export default api;
