import React, { createContext, useState, useContext, useEffect } from 'react';
import { authAPI } from '../services/api';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    checkAuth();
  }, []);

  const checkAuth = async () => {
    try {
      const token = localStorage.getItem('token');
      console.log('ðŸ” VÃ©rification auth - Token prÃ©sent:', !!token);
      
      if (token) {
        const response = await authAPI.getProfile();
        console.log('âœ… Profil rÃ©cupÃ©rÃ©:', response.data);
        
        if (response.data.success) {
          setUser(response.data.data.user);
          console.log('ðŸ‘¤ Utilisateur connectÃ©:', response.data.data.user.username);
        }
      }
    } catch (error) {
      console.error('âŒ Erreur vÃ©rification auth:', error);
      localStorage.removeItem('token');
      setUser(null);
    } finally {
      setLoading(false);
    }
  };

  const login = async (email, password) => {
    try {
      console.log('ðŸ” Tentative de connexion...', { email });
      setError('');
      const response = await authAPI.login(email, password);
      
      console.log('âœ… RÃ©ponse login:', response.data);
      
      if (response.data.success) {
        const { token, user } = response.data.data;
        console.log('ðŸ’¾ Sauvegarde du token:', token ? 'Token reÃ§u' : 'Aucun token');
        localStorage.setItem('token', token);
        setUser(user);
        console.log('ðŸŽ‰ Connexion rÃ©ussie pour:', user.username);
        return { success: true };
      }
    } catch (error) {
      console.error('âŒ Erreur connexion complÃ¨te:', error);
      console.error('ðŸ“¡ DÃ©tails erreur:', {
        status: error.response?.status,
        message: error.response?.data?.message,
        data: error.response?.data
      });
      
      const errorMessage = error.response?.data?.message || 'Erreur de connexion';
      setError(errorMessage);
      return { success: false, message: errorMessage };
    }
  };

  const register = async (username, email, password) => {
    try {
      console.log('ðŸ‘¤ Tentative d\'inscription...', { username, email });
      setError('');
      const response = await authAPI.register(username, email, password);
      
      console.log('âœ… RÃ©ponse inscription:', response.data);
      
      if (response.data.success) {
        const { token, user } = response.data.data;
        console.log('ðŸ’¾ Sauvegarde du token aprÃ¨s inscription');
        localStorage.setItem('token', token);
        setUser(user);
        return { success: true };
      }
    } catch (error) {
      console.error('âŒ Erreur inscription:', error);
      console.error('ðŸ“¡ DÃ©tails erreur:', {
        status: error.response?.status,
        message: error.response?.data?.message,
        data: error.response?.data
      });
      
      const errorMessage = error.response?.data?.message || 'Erreur d\'inscription';
      setError(errorMessage);
      return { success: false, message: errorMessage };
    }
  };

  const logout = () => {
    console.log('ðŸšª DÃ©connexion');
    localStorage.removeItem('token');
    setUser(null);
    setError('');
  };

  const clearError = () => {
    setError('');
  };

  const value = {
    user,
    loading,
    error,
    login,
    register,
    logout,
    clearError
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
