import React, { useState, useEffect } from 'react';

// Simple contexte d'authentification local
const AuthContext = React.createContext();

export const useAuth = () => {
  const context = React.useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      // Vérifier le token avec l'API
      checkAuth(token);
    }
  }, []);

  const checkAuth = async (token) => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:5000/api/auth/profile', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        if (data.success) {
          setUser(data.data.user);
        } else {
          localStorage.removeItem('token');
        }
      } else {
        localStorage.removeItem('token');
      }
    } catch (error) {
      console.error('Auth check failed:', error);
      localStorage.removeItem('token');
    } finally {
      setLoading(false);
    }
  };

  const login = async (email, password) => {
    try {
      setError('');
      setLoading(true);

      const response = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();

      if (data.success) {
        localStorage.setItem('token', data.data.token);
        setUser(data.data.user);
        return { success: true };
      } else {
        setError(data.message);
        return { success: false, message: data.message };
      }
    } catch (error) {
      setError('Erreur de connexion');
      return { success: false, message: 'Erreur de connexion' };
    } finally {
      setLoading(false);
    }
  };

  const register = async (username, email, password) => {
    try {
      setError('');
      setLoading(true);

      const response = await fetch('http://localhost:5000/api/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, email, password }),
      });

      const data = await response.json();

      if (data.success) {
        localStorage.setItem('token', data.data.token);
        setUser(data.data.user);
        return { success: true };
      } else {
        setError(data.message);
        return { success: false, message: data.message };
      }
    } catch (error) {
      setError('Erreur d\'inscription');
      return { success: false, message: 'Erreur d\'inscription' };
    } finally {
      setLoading(false);
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    setUser(null);
    setError('');
  };

  const clearError = () => setError('');

  return (
    <AuthContext.Provider value={{
      user,
      loading,
      error,
      login,
      register,
      logout,
      clearError
    }}>
      {children}
    </AuthContext.Provider>
  );
};

// Composant principal simplifié
function App() {
  const { user, login, register, logout, error, clearError } = useAuth();
  const [authMode, setAuthMode] = useState('login');
  const [authData, setAuthData] = useState({
    username: '',
    email: '',
    password: ''
  });

  const [rooms, setRooms] = useState([]);
  const [myRooms, setMyRooms] = useState([]);

  const handleAuthSubmit = async (e) => {
    e.preventDefault();
    if (authMode === 'login') {
      await login(authData.email, authData.password);
    } else {
      await register(authData.username, authData.email, authData.password);
    }
  };

  // Charger les salons quand l'utilisateur est connecté
  useEffect(() => {
    if (user) {
      loadRooms();
    }
  }, [user]);

  const loadRooms = async () => {
    try {
      const token = localStorage.getItem('token');

      // Charger les salons publics
      const publicResponse = await fetch('http://localhost:5000/api/chatrooms', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      const publicData = await publicResponse.json();
      if (publicData.success) {
        setRooms(publicData.data.chatRooms);
      }

      // Charger mes salons
      const myResponse = await fetch('http://localhost:5000/api/chatrooms/my', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      const myData = await myResponse.json();
      if (myData.success) {
        setMyRooms(myData.data.chatRooms);
      }
    } catch (error) {
      console.error('Error loading rooms:', error);
    }
  };

  const createRoom = async (name) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/chatrooms', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ name, description: '' })
      });

      const data = await response.json();
      if (data.success) {
        loadRooms(); // Recharger les salons
      }
      return data;
    } catch (error) {
      console.error('Error creating room:', error);
    }
  };

  const joinRoom = async (roomId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000/api/chatrooms/${roomId}/join`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      const data = await response.json();
      if (data.success) {
        loadRooms(); // Recharger les salons
      }
      return data;
    } catch (error) {
      console.error('Error joining room:', error);
    }
  };

  if (!user) {
    return (
      <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #667eea, #764ba2)',
        padding: '20px'
      }}>
        <div style={{
          background: 'white',
          padding: '30px',
          borderRadius: '10px',
          boxShadow: '0 4px 20px rgba(0,0,0,0.1)',
          width: '100%',
          maxWidth: '400px'
        }}>
          <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>
            {authMode === 'login' ? 'Connexion' : 'Inscription'}
          </h2>

          {error && (
            <div style={{
              background: '#f8d7da',
              color: '#721c24',
              padding: '12px',
              borderRadius: '5px',
              marginBottom: '15px'
            }}>
              {error}
              <button 
                onClick={clearError}
                style={{
                  background: 'none',
                  border: 'none',
                  float: 'right',
                  cursor: 'pointer'
                }}
              >
                ×
              </button>
            </div>
          )}

          <form onSubmit={handleAuthSubmit}>
            {authMode === 'register' && (
              <input
                type="text"
                placeholder="Nom d'utilisateur"
                value={authData.username}
                onChange={(e) => setAuthData({...authData, username: e.target.value})}
                required
                style={{
                  width: '100%',
                  padding: '12px',
                  marginBottom: '15px',
                  border: '1px solid #ddd',
                  borderRadius: '5px'
                }}
              />
            )}
            <input
              type="email"
              placeholder="Email"
              value={authData.email}
              onChange={(e) => setAuthData({...authData, email: e.target.value})}
              required
              style={{
                width: '100%',
                padding: '12px',
                marginBottom: '15px',
                border: '1px solid #ddd',
                borderRadius: '5px'
              }}
            />
            <input
              type="password"
              placeholder="Mot de passe"
              value={authData.password}
              onChange={(e) => setAuthData({...authData, password: e.target.value})}
              required
              style={{
                width: '100%',
                padding: '12px',
                marginBottom: '15px',
                border: '1px solid #ddd',
                borderRadius: '5px'
              }}
            />
            <button 
              type="submit"
              style={{
                width: '100%',
                padding: '12px',
                background: '#007bff',
                color: 'white',
                border: 'none',
                borderRadius: '5px',
                cursor: 'pointer'
              }}
            >
              {authMode === 'login' ? 'Se connecter' : 'S\'inscrire'}
            </button>
          </form>

          <button 
            onClick={() => {
              setAuthMode(authMode === 'login' ? 'register' : 'login');
              clearError();
            }}
            style={{
              background: 'none',
              border: 'none',
              color: '#007bff',
              cursor: 'pointer',
              marginTop: '15px',
              width: '100%'
            }}
          >
            {authMode === 'login' 
              ? 'Pas de compte ? S\'inscrire' 
              : 'Déjà un compte ? Se connecter'
            }
          </button>
        </div>
      </div>
    );
  }

  return (
    <div style={{ minHeight: '100vh', background: '#f5f5f5' }}>
      <header style={{
        background: '#007bff',
        color: 'white',
        padding: '15px 20px',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center'
      }}>
        <h1>Chat Application</h1>
        <div style={{ display: 'flex', alignItems: 'center', gap: '15px' }}>
          <span>Bonjour, {user.username}!</span>
          <button 
            onClick={logout}
            style={{
              background: '#6c757d',
              color: 'white',
              border: 'none',
              padding: '8px 16px',
              borderRadius: '5px',
              cursor: 'pointer'
            }}
          >
            Déconnexion
          </button>
        </div>
      </header>

      <main style={{ padding: '20px', maxWidth: '1200px', margin: '0 auto' }}>
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          marginBottom: '20px'
        }}>
          <h2>Salons de Discussion</h2>
          <button 
            onClick={() => {
              const name = prompt('Nom du salon:');
              if (name) createRoom(name);
            }}
            style={{
              background: '#007bff',
              color: 'white',
              border: 'none',
              padding: '10px 20px',
              borderRadius: '5px',
              cursor: 'pointer'
            }}
          >
            + Créer un Salon
          </button>
        </div>

        <div style={{ background: 'white', padding: '20px', borderRadius: '10px' }}>
          <h3>Mes Salons ({myRooms.length})</h3>
          {myRooms.length === 0 ? (
            <p>Vous n'êtes membre d'aucun salon</p>
          ) : (
            myRooms.map(room => (
              <div key={room._id} style={{
                border: '1px solid #ddd',
                padding: '15px',
                marginBottom: '10px',
                borderRadius: '5px',
                background: '#f9f9f9'
              }}>
                <h4>{room.name}</h4>
                {room.description && <p>{room.description}</p>}
                <small>Membres: {room.members.length}</small>
              </div>
            ))
          )}

          <h3 style={{ marginTop: '30px' }}>Salons Publics ({rooms.length})</h3>
          {rooms.length === 0 ? (
            <p>Aucun salon public</p>
          ) : (
            rooms.map(room => (
              <div key={room._id} style={{
                border: '1px solid #ddd',
                padding: '15px',
                marginBottom: '10px',
                borderRadius: '5px',
                background: '#f9f9f9'
              }}>
                <h4>{room.name}</h4>
                {room.description && <p>{room.description}</p>}
                <small>Créé par: {room.createdBy.username}</small>
                <br/>
                <small>Membres: {room.members.length}</small>
                <br/>
                <button 
                  onClick={() => joinRoom(room._id)}
                  style={{
                    background: '#28a745',
                    color: 'white',
                    border: 'none',
                    padding: '5px 10px',
                    borderRadius: '3px',
                    cursor: 'pointer',
                    marginTop: '10px'
                  }}
                >
                  Rejoindre
                </button>
              </div>
            ))
          )}
        </div>
      </main>
    </div>
  );
}

// Wrapper principal
export default function AppWrapper() {
  return (
    <AuthProvider>
      <App />
    </AuthProvider>
  );
}
