import React, { useState } from 'react';
import { AuthProvider, useAuth } from './contexts/AuthContext';
import './styles/chatRooms.css';

// Version simplifi√©e pour tester
function TestAuth() {
  const { user, login, register, logout, error } = useAuth();
  const [authMode, setAuthMode] = useState('login');
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: ''
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('üìù Soumission formulaire:', formData);

    if (authMode === 'login') {
      const result = await login(formData.email, formData.password);
      console.log('üîë R√©sultat connexion:', result);
    } else {
      const result = await register(formData.username, formData.email, formData.password);
      console.log('üë§ R√©sultat inscription:', result);
    }
  };

  if (user) {
    return (
      <div>
        <h1>Connect√© en tant que {user.username}</h1>
        <button onClick={logout}>D√©connexion</button>
      </div>
    );
  }

  return (
    <div className="auth-container">
      <div className="auth-form">
        <h2>{authMode === 'login' ? 'Connexion' : 'Inscription'}</h2>

        {error && (
          <div className="error-message">
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          {authMode === 'register' && (
            <input
              type="text"
              placeholder="Nom d'utilisateur"
              value={formData.username}
              onChange={(e) => setFormData({...formData, username: e.target.value})}
              required
            />
          )}
          <input
            type="email"
            placeholder="Email"
            value={formData.email}
            onChange={(e) => setFormData({...formData, email: e.target.value})}
            required
          />
          <input
            type="password"
            placeholder="Mot de passe"
            value={formData.password}
            onChange={(e) => setFormData({...formData, password: e.target.value})}
            required
          />
          <button type="submit">
            {authMode === 'login' ? 'Se connecter' : 'S\'inscrire'}
          </button>
        </form>

        <button 
          className="switch-mode"
          onClick={() => setAuthMode(authMode === 'login' ? 'register' : 'login')}
        >
          {authMode === 'login' 
            ? 'Pas de compte ? S\'inscrire' 
            : 'D√©j√† un compte ? Se connecter'
          }
        </button>
      </div>
    </div>
  );
}

function App() {
  return (
    <AuthProvider>
      <TestAuth />
    </AuthProvider>
  );
}

export default App;
