import React from 'react';
import { ThemeProvider } from './contexts/ThemeContext';
import { AuthProvider, useAuth } from './contexts/AuthContext';
import Login from './components/Auth/Login';
import './styles/globals.css';

// Composant principal qui utilise l'auth
const AppContent = () => {
  const { user, loading } = useAuth();

  if (loading) {
    return (
      <div style={{ 
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center', 
        height: '100vh',
        flexDirection: 'column',
        gap: '1rem'
      }}>
        <div style={{
          width: '40px',
          height: '40px',
          border: '3px solid #f3f3f3',
          borderTop: '3px solid #3b82f6',
          borderRadius: '50%',
          animation: 'spin 1s linear infinite'
        }}></div>
        <p>Chargement de ChatMe...</p>
      </div>
    );
  }

  return user ? (
    <div style={{ padding: '2rem', textAlign: 'center' }}>
      <h1>ðŸ’¬ ChatMe - Dashboard</h1>
      <p>Bienvenue {user.username} !</p>
      <p>L'interface complÃ¨te arrive bientÃ´t...</p>
    </div>
  ) : <Login />;
};

function App() {
  return (
    <ThemeProvider>
      <AuthProvider>
        <div className="App">
          <AppContent />
        </div>
      </AuthProvider>
    </ThemeProvider>
  );
}

export default App;
