/**
 * Contexte d'authentification React
 * Gère l'état global de l'authentification dans l'application
 * Fournit les fonctions de connexion, inscription et déconnexion
 */
import React, { createContext, useState, useContext, useEffect, useCallback } from 'react';
import { authAPI } from '../services/api';

// Création du contexte d'authentification
const AuthContext = createContext();

/**
 * Hook personnalisé pour utiliser le contexte d'authentification
 * @returns {Object} Contexte d'authentification avec user, loading, error et fonctions d'auth
 * @throws {Error} Si utilisé en dehors d'un AuthProvider
 */
export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

/**
 * Provider du contexte d'authentification
 * @param {Object} props - Props du composant
 * @param {ReactNode} props.children - Composants enfants à envelopper
 */
export const AuthProvider = ({ children }) => {
  // État de l'utilisateur connecté (null si non connecté)
  const [user, setUser] = useState(null);
  // État de chargement (true pendant les requêtes async)
  const [loading, setLoading] = useState(true);
  // État des erreurs d'authentification
  const [error, setError] = useState('');

  /**
   * Vérifie si l'utilisateur est authentifié au chargement de l'application
   * Utilise useCallback pour mémoriser la fonction et éviter les re-créations
   */
  const checkAuth = useCallback(async () => {
    try {
      // Récupère le token et les données utilisateur du localStorage
      const token = localStorage.getItem('token');
      const userData = localStorage.getItem('user');

      // Si token et données existent, vérifier avec le serveur
      if (token && userData) {
        try {
          const response = await authAPI.verify();
          if (response.data.success) {
            // Token valide, mettre à jour l'état utilisateur
            setUser(response.data.data.user);
          } else {
            // Token invalide côté serveur, déconnecter
            logout();
          }
        } catch (verifyError) {
          // Erreur de vérification, déconnecter
          console.error('Token verification failed:', verifyError);
          logout();
        }
      }
    } catch (error) {
      console.error('Auth check failed:', error);
      // En cas d'erreur, déconnecter pour être sûr
      logout();
    } finally {
      // Arrêter le loading quelle que soit l'issue
      setLoading(false);
    }
  }, []); // Dépendances vides car la fonction est stable

  // Vérifier l'authentification au chargement du composant
  useEffect(() => {
    checkAuth();
  }, [checkAuth]); // ✅ CORRECTION : checkAuth est maintenant dans les dépendances

  /**
   * Inscription d'un nouvel utilisateur
   * @param {Object} userData - Données du nouvel utilisateur {username, email, password}
   * @returns {Object} Résultat de l'opération {success: boolean, message?: string, data?: any}
   */
  const register = async (userData) => {
    try {
      setError(''); // Réinitialiser les erreurs
      setLoading(true); // Activer le loading

      const response = await authAPI.register(userData);
      
      if (response.data.success) {
        const { token, user } = response.data.data;
        
        // Sauvegarder les informations dans le localStorage
        localStorage.setItem('token', token);
        localStorage.setItem('user', JSON.stringify(user));
        setUser(user); // Mettre à jour l'état utilisateur
        
        return { success: true, data: response.data };
      }
    } catch (error) {
      // Gestion des erreurs d'API
      const errorMessage = error.response?.data?.message || 'Erreur lors de l\'inscription';
      setError(errorMessage);
      return { success: false, message: errorMessage };
    } finally {
      setLoading(false); // Désactiver le loading
    }
  };

  /**
   * Connexion d'un utilisateur existant
   * @param {Object} credentials - Identifiants de connexion {email, password}
   * @returns {Object} Résultat de l'opération {success: boolean, message?: string, data?: any}
   */
  const login = async (credentials) => {
    try {
      setError(''); // Réinitialiser les erreurs
      setLoading(true); // Activer le loading

      const response = await authAPI.login(credentials);
      
      if (response.data.success) {
        const { token, user } = response.data.data;
        
        // Sauvegarder les informations dans le localStorage
        localStorage.setItem('token', token);
        localStorage.setItem('user', JSON.stringify(user));
        setUser(user); // Mettre à jour l'état utilisateur
        
        return { success: true, data: response.data };
      }
    } catch (error) {
      // Gestion des erreurs d'API
      const errorMessage = error.response?.data?.message || 'Erreur lors de la connexion';
      setError(errorMessage);
      return { success: false, message: errorMessage };
    } finally {
      setLoading(false); // Désactiver le loading
    }
  };

  /**
   * Déconnexion de l'utilisateur
   * Nettoie le localStorage et remet à zéro l'état
   */
  const logout = async () => {
    try {
      // Appeler l'API de déconnexion côté serveur si un token existe
      const token = localStorage.getItem('token');
      if (token) {
        await authAPI.logout();
      }
    } catch (error) {
      console.error('Logout API error:', error);
      // Continuer quand même le nettoyage local en cas d'erreur
    } finally {
      // Nettoyer le localStorage et remettre à zéro l'état
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      setUser(null);
      setError('');
      setLoading(false);
    }
  };

  /**
   * Effacer les messages d'erreur
   */
  const clearError = () => {
    setError('');
  };

  // Valeur du contexte à fournir aux composants enfants
  const value = {
    user,           // Utilisateur connecté ou null
    loading,        // État de chargement
    error,          // Message d'erreur
    register,       // Fonction d'inscription
    login,          // Fonction de connexion
    logout,         // Fonction de déconnexion
    clearError,     // Fonction pour effacer les erreurs
    isAuthenticated: !!user // Booléen indiquant si l'utilisateur est connecté
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
