import axios from 'axios';

// Configuration de base de l'API
const API_URL = 'http://localhost:5000/api';

// CrÃ©ation de l'instance axios
const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Fonction pour obtenir le token
const getToken = () => {
  return localStorage.getItem('token');
};

// Intercepteur pour ajouter le token aux requÃªtes
api.interceptors.request.use(
  (config) => {
    const token = getToken();
    console.log('ðŸ” Token utilisÃ©:', token ? 'OUI' : 'NON');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    console.error('âŒ Erreur intercepteur request:', error);
    return Promise.reject(error);
  }
);

// Intercepteur pour gÃ©rer les erreurs de rÃ©ponse
api.interceptors.response.use(
  (response) => {
    console.log('âœ… RÃ©ponse API rÃ©ussie:', response.config.url);
    return response;
  },
  (error) => {
    console.error('âŒ Erreur API:', error.response?.status, error.config?.url);
    
    if (error.response?.status === 401) {
      console.log('ðŸ” Token expirÃ© ou invalide');
      localStorage.removeItem('token');
      // Ne pas rediriger automatiquement pour Ã©viter les boucles
    }
    return Promise.reject(error);
  }
);

// Services pour l'authentification
export const authAPI = {
  login: (email, password) => {
    console.log('ðŸ” Login API appelÃ©');
    return api.post('/auth/login', { email, password });
  },
  register: (username, email, password) => {
    console.log('ðŸ‘¤ Register API appelÃ©');
    return api.post('/auth/register', { username, email, password });
  },
  getProfile: () => {
    console.log('ðŸ“‹ GetProfile API appelÃ©');
    return api.get('/auth/profile');
  },
};

// Services pour les salons de discussion
export const chatRoomAPI = {
  create: (roomData) => {
    console.log('ðŸš€ API - CrÃ©ation salon:', roomData);
    return api.post('/chatrooms', roomData);
  },
  getAll: (params) => {
    console.log('ðŸš€ API - RÃ©cupÃ©ration salons publics');
    return api.get('/chatrooms', { params });
  },
  getMyRooms: () => {
    console.log('ðŸš€ API - RÃ©cupÃ©ration mes salons');
    return api.get('/chatrooms/my');
  },
  join: (roomId) => {
    console.log('ðŸš€ API - Rejoindre salon:', roomId);
    return api.post(`/chatrooms/${roomId}/join`);
  },
  leave: (roomId) => {
    console.log('ðŸš€ API - Quitter salon:', roomId);
    return api.post(`/chatrooms/${roomId}/leave`);
  },
};

// Fonction de test
export const testAPI = () => {
  const token = getToken();
