import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';

// Contexte d'authentification
const AuthContext = React.createContext();

export const useAuth = () => {
  const context = React.useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  const login = async (email, password) => {
    try {
      console.log('ğŸ” Tentative de connexion avec:', email);

      const response = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();
      console.log('ğŸ“¨ RÃ©ponse login:', data);

      if (data.success) {
        localStorage.setItem('token', data.data.token);
        localStorage.setItem('user', JSON.stringify(data.data.user));
        setUser(data.data.user);
        return { success: true };
      }
      return { success: false, message: data.message };
    } catch (error) {
      console.error('âŒ Erreur connexion:', error);
      return { success: false, message: 'Erreur de connexion au serveur' };
    }
  };

  const register = async (username, email, password) => {
    try {
      console.log('ğŸ“ Tentative d\'inscription:', username, email);

      const response = await fetch('http://localhost:5000/api/auth/register', {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ username, email, password }),
      });

      const data = await response.json();
      console.log('ğŸ“¨ RÃ©ponse register:', data);

      if (data.success) {
        localStorage.setItem('token', data.data.token);
        localStorage.setItem('user', JSON.stringify(data.data.user));
        setUser(data.data.user);
        return { success: true };
      }
      return { success: false, message: data.message };
    } catch (error) {
      console.error('âŒ Erreur inscription:', error);
      return { success: false, message: 'Erreur d\'inscription au serveur' };
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
  };

  useEffect(() => {
    const checkAuth = async () => {
      const token = localStorage.getItem('token');
      const storedUser = localStorage.getItem('user');

      if (token && storedUser) {
        try {
          // VÃ©rifier que le token est toujours valide
          const response = await fetch('http://localhost:5000/api/auth/profile', {
            headers: { 
              'Authorization': `Bearer ${token}`,
              'Content-Type': 'application/json'
            }
          });

          if (response.ok) {
            const data = await response.json();
            if (data.success) {
              setUser(data.data.user);
            } else {
              // Token invalide, dÃ©connexion
              logout();
            }
          } else {
            // Erreur serveur, utiliser les donnÃ©es stockÃ©es
            setUser(JSON.parse(storedUser));
          }
        } catch (error) {
          console.log('âš ï¸ Serveur indisponible, utilisation donnÃ©es stockÃ©es');
          setUser(JSON.parse(storedUser));
        }
      }
      setLoading(false);
    };

    checkAuth();
  }, []);

  if (loading) {
    return (
      <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '100vh',
        background: '#f5f5f5'
      }}>
        <div style={{ textAlign: 'center' }}>
          <div style={{ fontSize: '24px', marginBottom: '10px' }}>â³</div>
          <div>Chargement...</div>
        </div>
      </div>
    );
  }

  return (
    <AuthContext.Provider value={{ user, login, register, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

// Composant de connexion
function LoginForm() {
  const { login, register } = useAuth();
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({ username: '', email: '', password: '' });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    console.log('ğŸ”„ Soumission formulaire...');

    const result = isLogin 
      ? await login(formData.email, formData.password)
      : await register(formData.username, formData.email, formData.password);

    setLoading(false);

    if (!result.success) {
      setError(result.message || 'Erreur inconnue');
    }
  };

  return (
    <div style={{
      display: 'flex', 
      justifyContent: 'center', 
      alignItems: 'center', 
      minHeight: '100vh', 
      background: 'linear-gradient(135deg, #667eea, #764ba2)'
    }}>
      <div style={{
        background: 'white', 
        padding: '30px', 
        borderRadius: '10px', 
        boxShadow: '0 4px 20px rgba(0,0,0,0.1)', 
        width: '100%', 
        maxWidth: '400px'
      }}>
        <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>
          {isLogin ? 'Connexion' : 'Inscription'}
        </h2>

        {error && (
          <div style={{
            background: '#f8d7da', 
            color: '#721c24', 
            padding: '12px',
            borderRadius: '5px', 
            marginBottom: '15px',
            fontSize: '14px'
          }}>
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          {!isLogin && (
            <input
              type="text"
              placeholder="Nom d'utilisateur"
              value={formData.username}
              onChange={(e) => setFormData({...formData, username: e.target.value})}
              required
              style={{
                width: '100%', 
                padding: '12px', 
                marginBottom: '15px',
                border: '1px solid #ddd', 
                borderRadius: '5px',
                fontSize: '16px'
              }}
            />
          )}
          <input
            type="email"
            placeholder="Email"
            value={formData.email}
            onChange={(e) => setFormData({...formData, email: e.target.value})}
            required
            style={{
              width: '100%', 
              padding: '12px', 
              marginBottom: '15px',
              border: '1px solid #ddd', 
              borderRadius: '5px',
              fontSize: '16px'
            }}
          />
          <input
            type="password"
            placeholder="Mot de passe"
            value={formData.password}
            onChange={(e) => setFormData({...formData, password: e.target.value})}
            required
            style={{
              width: '100%', 
              padding: '12px', 
              marginBottom: '15px',
              border: '1px solid #ddd', 
              borderRadius: '5px',
              fontSize: '16px'
            }}
          />
          <button 
            type="submit" 
            disabled={loading}
            style={{
              width: '100%', 
              padding: '12px', 
              background: loading ? '#6c757d' : '#007bff',
              color: 'white', 
              border: 'none', 
              borderRadius: '5px', 
              cursor: loading ? 'not-allowed' : 'pointer',
              fontSize: '16px',
              opacity: loading ? 0.7 : 1
            }}
          >
            {loading ? 'â³ Chargement...' : (isLogin ? 'ğŸ” Se connecter' : 'ğŸ“ S\'inscrire')}
          </button>
        </form>
        <button 
          onClick={() => { setIsLogin(!isLogin); setError(''); }} 
          style={{
            background: 'none', 
            border: 'none', 
            color: '#007bff',
            cursor: 'pointer', 
            marginTop: '15px', 
            width: '100%',
            fontSize: '14px',
            padding: '10px'
          }}
        >
          {isLogin ? 'ğŸ“ Pas de compte ? S\'inscrire' : 'ğŸ” DÃ©jÃ  un compte ? Se connecter'}
        </button>

        <div style={{
          marginTop: '20px',
          padding: '15px',
          background: '#e7f3ff',
          borderRadius: '5px',
          fontSize: '12px',
          color: '#0066cc',
          textAlign: 'center'
        }}>
          <strong>ğŸš€ Mode Temps RÃ©el Actif</strong><br/>
          Backend + Socket.IO sur localhost:5000
        </div>
      </div>
    </div>
  );
}
// Composant de Chat avec gestion d'erreur amÃ©liorÃ©e
function ChatRoom({ room, onClose }) {
  const { user } = useAuth();
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [socket, setSocket] = useState(null);
  const [isConnected, setIsConnected] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState('connecting');

  useEffect(() => {
    console.log('ğŸ’¬ Initialisation Socket.IO pour le salon:', room.name);

    let newSocket;

    try {
      // Initialiser la connexion Socket.IO
      const token = localStorage.getItem('token');
      console.log('ğŸ”Œ Tentative de connexion Socket.IO...');

      newSocket = io('http://localhost:5000', {
        auth: {
          token: token || 'demo-token'
        },
        timeout: 5000, // Timeout de 5 secondes
        transports: ['websocket', 'polling'] // Essayer les deux transports
      });

      newSocket.on('connect', () => {
        console.log('ğŸ”Œ ConnectÃ© au serveur Socket.IO');
        setIsConnected(true);
        setConnectionStatus('connected');

        // Rejoindre le room
        newSocket.emit('join_room', room._id);
        console.log(`ğŸšª Rejoint le salon Socket.IO: ${room._id}`);
      });

      newSocket.on('disconnect', (reason) => {
        console.log('ğŸ”Œ DÃ©connectÃ© du serveur Socket.IO:', reason);
        setIsConnected(false);
        setConnectionStatus('disconnected');
      });

      newSocket.on('connect_error', (error) => {
        console.error('âŒ Erreur connexion Socket.IO:', error.message);
        setIsConnected(false);
        setConnectionStatus('error');

        // Fallback vers le mode local aprÃ¨s 3 secondes
        setTimeout(() => {
          if (!isConnected) {
            setConnectionStatus('local');
            console.log('ğŸ”„ Passage en mode local');
          }
        }, 3000);
      });

      // Ã‰couter les nouveaux messages temps rÃ©el
      newSocket.on('new_message', (message) => {
        console.log('ğŸ’¬ Nouveau message temps rÃ©el:', message);
        setMessages(prev => {
          const filtered = prev.filter(msg => !msg.isTemporary);
          return [...filtered, message];
        });
      });

      setSocket(newSocket);

    } catch (error) {
      console.error('âŒ Erreur initialisation Socket.IO:', error);
      setConnectionStatus('local');
    }

    // Charger l'historique des messages
    loadMessageHistory(room._id);

    // Nettoyer Ã  la dÃ©connexion
    return () => {
      console.log('ğŸ§¹ Nettoyage Socket.IO');
      if (newSocket) {
        newSocket.emit('leave_room', room._id);
        newSocket.disconnect();
      }
    };
  }, [room, isConnected, loadMessageHistory]);

  const loadMessageHistory = async (roomId) => {
    try {
      const token = localStorage.getItem('token');
      console.log('ğŸ“¡ Chargement historique messages pour:', roomId, token ? '(token prÃ©sent)' : '(pas de token)');

      // Messages de dÃ©mo par dÃ©faut
      const demoMessages = [
        {
          _id: 1,
          content: `Bienvenue dans le salon ${room.name} ! ğŸ‰`,
          user: { _id: 'system', username: 'SystÃ¨me' },
          createdAt: new Date(),
          isSystem: true
        },
        {
          _id: 2,
          content: connectionStatus === 'connected' 
            ? 'Socket.IO connectÃ© - Temps rÃ©el activÃ© !' 
            : 'Mode local - Messages simulÃ©s',
          user: { _id: 'system', username: 'SystÃ¨me' },
          createdAt: new Date(),
          isSystem: true
        }
      ];

      setMessages(demoMessages);

    } catch (error) {
      console.log('âŒ Erreur chargement historique:', error);
    }
  };

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (!newMessage.trim()) return;

    console.log('ğŸ“¤ Envoi message:', newMessage);

    // CrÃ©er un message temporaire pour l'optimistic update
    const tempMessage = {
      _id: `temp-${Date.now()}`,
      content: newMessage.trim(),
      user: user,
      createdAt: new Date(),
      isTemporary: true
    };

    // Afficher le message immÃ©diatement
    setMessages(prev => [...prev, tempMessage]);
    setNewMessage('');

    if (isConnected && socket) {
      // Envoyer le message via Socket.IO si connectÃ©
      socket.emit('send_message', {
        roomId: room._id,
        content: newMessage.trim()
      });

      // Le vrai message arrivera via l'Ã©vÃ©nement 'new_message'
    } else {
      // Mode local: simuler une rÃ©ponse aprÃ¨s 1 seconde
      setTimeout(() => {
        const responseMessage = {
          _id: Date.now(),
          content: `[Mode Local] RÃ©ponse Ã : "${newMessage.trim()}"`,
          user: { _id: 'bot', username: 'Bot Local' },
          createdAt: new Date()
        };
        setMessages(prev => {
          const filtered = prev.filter(msg => msg._id !== tempMessage._id);
          return [...filtered, responseMessage];
        });
      }, 1000);
    }
  };

  const getStatusInfo = () => {
    switch(connectionStatus) {
      case 'connected':
        return { color: '#28a745', text: 'ğŸ”µ Temps RÃ©el Actif', message: 'Socket.IO ConnectÃ©' };
      case 'connecting':
        return { color: '#ffc107', text: 'ğŸŸ¡ Connexion...', message: 'Tentative de connexion' };
      case 'error':
        return { color: '#dc3545', text: 'ğŸ”´ Hors Ligne', message: 'Erreur de connexion' };
      case 'local':
        return { color: '#6c757d', text: 'âšª Mode Local', message: 'Messages simulÃ©s' };
      default:
        return { color: '#6c757d', text: 'âš« Inconnu', message: 'Statut indÃ©terminÃ©' };
    }
  };

  const statusInfo = getStatusInfo();

  const formatTime = (date) => {
    if (typeof date === 'string') date = new Date(date);
    return date.toLocaleTimeString('fr-FR', { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  const formatDate = (date) => {
    if (typeof date === 'string') date = new Date(date);
    return date.toLocaleDateString('fr-FR');
  };

  const groupMessagesByDate = () => {
    const groups = {};
    messages.forEach(message => {
      const date = formatDate(message.createdAt);
      if (!groups[date]) groups[date] = [];
      groups[date].push(message);
    });
    return groups;
  };

  const messageGroups = groupMessagesByDate();

  return (
    <div style={{
      display: 'flex',
      flexDirection: 'column',
      height: '600px',
      border: '1px solid #e9ecef',
      borderRadius: '8px',
      background: 'white',
      marginLeft: '20px',
      flex: 2,
      minWidth: '500px'
    }}>
      {/* En-tÃªte du chat */}
      <div style={{
        padding: '15px 20px',
        borderBottom: '1px solid #e9ecef',
        background: '#f8f9fa',
        borderRadius: '8px 8px 0 0',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center'
      }}>
        <div>
          <h3 style={{ margin: 0, color: '#333' }}>#{room.name}</h3>
          <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
            <small style={{ color: '#6c757d' }}>
              {room.members?.length || 1} membre(s)
            </small>
            <div style={{
              width: '8px',
              height: '8px',
              borderRadius: '50%',
              background: statusInfo.color
            }} />
            <small style={{ 
              color: statusInfo.color,
              fontWeight: 'bold'
            }}>
              {statusInfo.text}
            </small>
          </div>
        </div>
        <button 
          onClick={onClose}
          style={{
            background: '#6c757d',
            color: 'white',
            border: 'none',
            padding: '8px 12px',
            borderRadius: '5px',
            cursor: 'pointer'
          }}
        >
          âœ• Fermer
        </button>
      </div>

      {/* Zone des messages */}
      <div style={{
        flex: 1,
        overflowY: 'auto',
        padding: '20px',
        background: '#fafafa'
      }}>
        {messages.length === 0 ? (
          <div style={{
            textAlign: 'center',
            color: '#666',
            fontStyle: 'italic',
            marginTop: '50px'
          }}>
            <div style={{ fontSize: '48px', marginBottom: '10px' }}>ğŸ’¬</div>
            <div>Aucun message dans ce salon.</div>
            <div>Soyez le premier Ã  envoyer un message !</div>
          </div>
        ) : (
          Object.entries(messageGroups).map(([date, dayMessages]) => (
            <div key={date}>
              <div style={{
                textAlign: 'center',
                margin: '20px 0',
                position: 'relative'
              }}>
                <div style={{
                  display: 'inline-block',
                  background: '#e9ecef',
                  color: '#6c757d',
                  padding: '5px 15px',
                  borderRadius: '15px',
                  fontSize: '12px',
                  fontWeight: 'bold'
                }}>
                  {date}
                </div>
              </div>

              {dayMessages.map((message) => (
                <div
                  key={message._id}
                  style={{
                    marginBottom: '15px',
                    padding: message.isSystem ? '10px 15px' : '12px 16px',
                    borderRadius: message.isSystem ? '5px' : '12px',
                    background: message.isSystem ? '#e9ecef' : 
                               message.user._id === user._id ? '#007bff' : 'white',
                    color: message.isSystem ? '#6c757d' : 
                          message.user._id === user._id ? 'white' : '#333',
                    border: message.isSystem ? 'none' : 
                           message.user._id === user._id ? 'none' : '1px solid #e9ecef',
                    maxWidth: message.isSystem ? '100%' : '75%',
                    alignSelf: message.user._id === user._id ? 'flex-end' : 'flex-start',
                    marginLeft: message.user._id === user._id ? 'auto' : '0',
                    marginRight: message.user._id === user._id ? '0' : 'auto',
                    fontStyle: message.isSystem ? 'italic' : 'normal',
                    opacity: message.isTemporary ? 0.7 : 1,
                    transition: 'all 0.3s ease'
                  }}
                >
                  {!message.isSystem && (
                    <div style={{
                      display: 'flex',
                      justifyContent: 'space-between',
                      alignItems: 'flex-start',
                      marginBottom: '5px'
                    }}>
                      <strong style={{
                        fontSize: '14px',
                        color: message.user._id === user._id ? 'white' : '#007bff'
                      }}>
                        {message.user._id === user._id ? 'Vous' : message.user.username}
                        {message.isTemporary && ' (envoi...)'}
                      </strong>
                      <small style={{
                        opacity: 0.8,
                        fontSize: '11px',
                        marginLeft: '10px'
                      }}>
                        {formatTime(message.createdAt)}
                      </small>
                    </div>
                  )}
                  <div style={{ 
                    lineHeight: '1.4',
                    fontSize: message.isSystem ? '13px' : '14px'
                  }}>
                    {message.content}
                  </div>
                </div>
              ))}
            </div>
          ))
        )}
      </div>

      {/* Zone de saisie */}
      <form onSubmit={handleSendMessage} style={{
        padding: '20px',
        borderTop: '1px solid #e9ecef',
        background: 'white',
        borderRadius: '0 0 8px 8px'
      }}>
        <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
          <input
            type="text"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder={
              connectionStatus === 'connected' ? "Tapez votre message (EntrÃ©e pour envoyer)..." :
              connectionStatus === 'connecting' ? "Connexion au serveur..." :
              connectionStatus === 'error' ? "Serveur hors ligne - Mode local..." :
              "Mode local - Messages simulÃ©s..."
            }
            disabled={connectionStatus === 'connecting'}
            style={{
              flex: 1,
              padding: '12px 15px',
              border: `1px solid ${connectionStatus === 'connected' ? '#28a745' : connectionStatus === 'connecting' ? '#ffc107' : '#dc3545'}`,
              borderRadius: '20px',
              outline: 'none',
              fontSize: '14px',
              background: connectionStatus === 'connecting' ? '#fff3cd' : 'white'
            }}
            onKeyPress={(e) => {
              if (e.key === 'Enter' && !e.shiftKey) {
                e.preventDefault();
                handleSendMessage(e);
              }
            }}
          />
          <button
            type="submit"
            disabled={!newMessage.trim() || connectionStatus === 'connecting'}
            style={{
              padding: '12px 20px',
              background: (newMessage.trim() && connectionStatus !== 'connecting') ? 
                         (connectionStatus === 'connected' ? '#28a745' : '#6c757d') : '#ccc',
              color: 'white',
              border: 'none',
              borderRadius: '20px',
              cursor: (newMessage.trim() && connectionStatus !== 'connecting') ? 'pointer' : 'not-allowed',
              fontSize: '14px'
            }}
          >
            {connectionStatus === 'connecting' ? 'âŒ›' : 
             connectionStatus === 'connected' ? 'ğŸš€' : 'ğŸ“'}
          </button>
        </div>
        <div style={{
          textAlign: 'center',
          color: statusInfo.color,
          fontSize: '12px',
          marginTop: '8px',
          fontWeight: 'bold'
        }}>
          {statusInfo.message}
          {connectionStatus === 'error' && ' - Utilisation du mode local'}
        </div>
      </form>
    </div>
  );
}
