import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import './Auth.css';

const Login = () => {
  const [isLogin, setIsLogin] = useState(true);
  
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: ''
  });
  const [formErrors, setFormErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  
  const { login, register, loading, error, clearError } = useAuth();

  // Effacer les erreurs quand on change de mode
  useEffect(() => {
    clearError();
    setFormErrors({});
    setFormData({
      username: '',
      email: '',
      password: ''
    });
  }, [isLogin, clearError]);

  /**
   * Validation du formulaire
   */
  const validateForm = () => {
    const errors = {};

    if (!isLogin) {
      // Validation pour l'inscription
      if (!formData.username.trim()) {
        errors.username = 'Le nom d\'utilisateur est requis';
      } else if (formData.username.length < 3) {
        errors.username = 'Le nom d\'utilisateur doit contenir au moins 3 caract√®res';
      }
    }

    if (!formData.email.trim()) {
      errors.email = 'L\'email est requis';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      errors.email = 'Format d\'email invalide';
    }

    if (!formData.password) {
      errors.password = 'Le mot de passe est requis';
    } else if (formData.password.length < 6) {
      errors.password = 'Le mot de passe doit contenir au moins 6 caract√®res';
    }

    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  /**
   * Gestion de la soumission du formulaire
   */
  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log(' Tentative de soumission du formulaire...');
    console.log(' Mode:', isLogin ? 'CONNEXION' : 'INSCRIPTION');
    
    if (!validateForm()) {
      console.log(' Formulaire invalide');
      return;
    }

    setIsSubmitting(true);

    try {
      //  CORRECTION : Pr√©parer les donn√©es selon le mode
      let requestData;
      
      if (isLogin) {
        // MODE CONNEXION - seulement email et password
        requestData = {
          email: formData.email,
          password: formData.password
        };
      } else {
        // MODE INSCRIPTION - tous les champs
        requestData = {
          username: formData.username,
          email: formData.email,
          password: formData.password
        };
      }

      console.log(' Donn√©es envoy√©es:', requestData);

      const result = isLogin 
        ? await login(requestData)
        : await register(requestData);

      console.log(' R√©sultat de l\'authentification:', result);

      if (result.success) {
        console.log(' Authentification r√©ussie!');
      } else {
        console.log('‚ùå Erreur d\'authentification:', result.message);
      }
    } catch (err) {
      console.error('üí• Erreur lors de la soumission:', err);
    } finally {
      setIsSubmitting(false);
    }
  };

  /**
   * Gestion du changement des champs
   */
  const handleChange = (e) => {
    const { name, value } = e.target;
    console.log(`üìù Champ ${name} modifi√©:`, value);
    
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Effacer l'erreur du champ quand l'utilisateur tape
    if (formErrors[name]) {
      setFormErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }

    // Effacer les erreurs globales
    if (error) {
      clearError();
    }
  };

  /**
   * Bascule entre connexion et inscription
   */
  const toggleMode = () => {
    console.log(' Changement de mode:', isLogin ? 'inscription' : 'connexion');
    setIsLogin(!isLogin);
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <div className="auth-header">
          <h1>ChatMe</h1>
          <p>{isLogin ? 'Connectez-vous √† votre compte' : 'Cr√©ez votre compte'}</p>
        </div>

        {/* Message d'erreur global */}
        {error && (
          <div className="error-message global-error">
             {error}
          </div>
        )}

        <form onSubmit={handleSubmit} className="auth-form">
          {/* Champ username seulement pour l'inscription */}
          {!isLogin && (
            <div className="form-group">
              <label htmlFor="username">Nom d'utilisateur</label>
              <input
                type="text"
                id="username"
                name="username"
                value={formData.username}
                onChange={handleChange}
                placeholder="Votre nom d'utilisateur"
                className={formErrors.username ? 'error' : ''}
                disabled={isSubmitting || loading}
              />
              {formErrors.username && (
                <span className="field-error">{formErrors.username}</span>
              )}
            </div>
          )}
          
          {/* Champ email */}
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="votre@email.com"
              className={formErrors.email ? 'error' : ''}
              disabled={isSubmitting || loading}
            />
            {formErrors.email && (
              <span className="field-error">{formErrors.email}</span>
            )}
          </div>

          {/* Champ mot de passe */}
          <div className="form-group">
            <label htmlFor="password">Mot de passe</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="Votre mot de passe"
              className={formErrors.password ? 'error' : ''}
              disabled={isSubmitting || loading}
            />
            {formErrors.password && (
              <span className="field-error">{formErrors.password}</span>
            )}
          </div>

          {/* Bouton de soumission */}
          <button 
            type="submit" 
            className="auth-button"
            disabled={isSubmitting || loading}
          >
            {isSubmitting || loading ? (
              <>
                <span className="loading-spinner"></span>
                Chargement...
              </>
            ) : (
              isLogin ? 'Se connecter' : 'Cr√©er un compte'
            )}
          </button>
        </form>

        {/* Lien pour basculer entre connexion et inscription */}
        <div className="auth-footer">
          <p>
            {isLogin ? "Pas de compte ? " : "D√©j√† un compte ? "}
            <button 
              type="button" 
              className="link-button"
              onClick={toggleMode}
              disabled={isSubmitting || loading}
            >
              {isLogin ? 'Cr√©er un compte' : 'Se connecter'}
            </button>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
