import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';

// ==================== THEME CONTEXT ====================
const ThemeContext = React.createContext();

export const useTheme = () => {
  const context = React.useContext(ThemeContext);
  if (!context) {
    throw new Error('useTheme must be used within a ThemeProvider');
  }
  return context;
};

export const ThemeProvider = ({ children }) => {
  const [theme, setTheme] = useState('light');

  useEffect(() => {
    const savedTheme = localStorage.getItem('chatme-theme');
    if (savedTheme) {
      setTheme(savedTheme);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('chatme-theme', theme);
    document.documentElement.setAttribute('data-theme', theme);
  }, [theme]);

  const toggleTheme = () => {
    setTheme(prev => prev === 'light' ? 'dark' : 'light');
  };

  const value = {
    theme,
    toggleTheme,
    isDark: theme === 'dark'
  };

  return (
    <ThemeContext.Provider value={value}>
      {children}
    </ThemeContext.Provider>
  );
};
// ==================== FIN THEME CONTEXT ====================

// Contexte d'authentification
const AuthContext = React.createContext();

export const useAuth = () => {
  const context = React.useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  const login = async (email, password) => {
    try {
      console.log(' Tentative de connexion avec:', email);

      const response = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();
      console.log(' RÃ©ponse login:', data);

      if (data.success) {
        localStorage.setItem('token', data.data.token);
        localStorage.setItem('user', JSON.stringify(data.data.user));
        setUser(data.data.user);
        return { success: true };
      }
      return { success: false, message: data.message };
    } catch (error) {
      console.error(' Erreur connexion:', error);
      return { success: false, message: 'Erreur de connexion au serveur' };
    }
  };

  const register = async (username, email, password) => {
    try {
      console.log(' Tentative d\'inscription:', username, email);

      const response = await fetch('http://localhost:5000/api/auth/register', {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ username, email, password }),
      });

      const data = await response.json();
      console.log(' RÃ©ponse register:', data);

      if (data.success) {
        localStorage.setItem('token', data.data.token);
        localStorage.setItem('user', JSON.stringify(data.data.user));
        setUser(data.data.user);
        return { success: true };
      }
      return { success: false, message: data.message };
    } catch (error) {
      console.error(' Erreur inscription:', error);
      return { success: false, message: 'Erreur d\'inscription au serveur' };
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
  };

  useEffect(() => {
    const checkAuth = async () => {
      const token = localStorage.getItem('token');
      const storedUser = localStorage.getItem('user');

      if (token && storedUser) {
        try {
          // VÃ©rifier que le token est toujours valide
          const response = await fetch('http://localhost:5000/api/auth/profile', {
            headers: { 
              'Authorization': `Bearer ${token}`,
              'Content-Type': 'application/json'
            }
          });

          if (response.ok) {
            const data = await response.json();
            if (data.success) {
              setUser(data.data.user);
            } else {
              // Token invalide, dÃ©connexion
              logout();
            }
          } else {
            // Erreur serveur, utiliser les donnÃ©es stockÃ©es
            setUser(JSON.parse(storedUser));
          }
        } catch (error) {
          console.log('âš ï¸ Serveur indisponible, utilisation donnÃ©es stockÃ©es');
          setUser(JSON.parse(storedUser));
        }
      }
      setLoading(false);
    };

    checkAuth();
  }, []);

  if (loading) {
    return (
      <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '100vh',
        background: 'linear-gradient(135deg, var(--primary-500) 0%, var(--primary-700) 100%)',
        color: 'white'
      }}>
        <div style={{ textAlign: 'center' }}>
          <div style={{ 
            fontSize: '48px', 
            marginBottom: '20px',
            animation: 'pulse 2s infinite' 
          }}>ðŸ’¬</div>
          <div style={{ fontSize: '18px', marginBottom: '10px' }}>ChatMe</div>
          <div>Chargement...</div>
          <style>
            {`
              @keyframes pulse {
                0%, 100% { opacity: 1; transform: scale(1); }
                50% { opacity: 0.7; transform: scale(1.1); }
              }
            `}
          </style>
        </div>
      </div>
    );
  }

  return (
    <AuthContext.Provider value={{ user, login, register, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

// Composant de connexion
function LoginForm() {
  const { login, register } = useAuth();
  const { theme, toggleTheme } = useTheme();
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({ username: '', email: '', password: '' });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    console.log(' Soumission formulaire...');

    const result = isLogin 
      ? await login(formData.email, formData.password)
      : await register(formData.username, formData.email, formData.password);

    setLoading(false);

    if (!result.success) {
      setError(result.message || 'Erreur inconnue');
    }
  };

  return (
    <div style={{
      display: 'flex', 
      justifyContent: 'center', 
      alignItems: 'center', 
      minHeight: '100vh', 
      background: 'linear-gradient(135deg, var(--primary-500) 0%, var(--primary-700) 100%)',
      padding: 'var(--space-6)',
      position: 'relative'
    }}>
      {/* Bouton thÃ¨me */}
      <button 
        onClick={toggleTheme}
        style={{
          position: 'absolute',
          top: 'var(--space-6)',
          right: 'var(--space-6)',
          background: 'rgba(255, 255, 255, 0.2)',
          border: 'none',
          borderRadius: '50%',
          width: '40px',
          height: '40px',
          fontSize: '1.2rem',
          cursor: 'pointer',
          transition: 'var(--transition)',
          backdropFilter: 'blur(10px)'
        }}
        title={`Passer en mode ${theme === 'light' ? 'sombre' : 'clair'}`}
      >
        {theme === 'light' ? 'ðŸŒ™' : 'â˜€ï¸'}
      </button>

      <div style={{
        background: 'white', 
        padding: 'var(--space-8)', 
        borderRadius: 'var(--radius-xl)', 
        boxShadow: 'var(--shadow-lg)', 
        width: '100%', 
        maxWidth: '400px',
        border: '1px solid var(--secondary-200)'
      }} className="fade-in">
        <h2 style={{ 
          textAlign: 'center', 
          marginBottom: '20px',
          color: 'var(--secondary-900)',
          fontSize: 'var(--font-size-2xl)'
        }}>
          {isLogin ? 'ðŸ’¬ ChatMe Connexion' : 'ðŸ’¬ Inscription'}
        </h2>

        {error && (
          <div style={{
            background: 'var(--error-50, #f8d7da)', 
            color: 'var(--error-600, #721c24)', 
            padding: '12px',
            borderRadius: 'var(--radius)', 
            marginBottom: '15px',
            fontSize: 'var(--font-size-sm)',
            border: '1px solid var(--error-200, #f5c6cb)'
          }}>
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          {!isLogin && (
            <input
              type="text"
              placeholder="Nom d'utilisateur"
              value={formData.username}
              onChange={(e) => setFormData({...formData, username: e.target.value})}
              required
