    }}>
      {/* En-tÃªte du chat */}
      <div style={{
        padding: '15px 20px',
        borderBottom: '1px solid var(--secondary-200)',
        background: 'var(--secondary-100)',
        borderRadius: 'var(--radius-lg) var(--radius-lg) 0 0',
        display: 'flex',
