            style={{
              flex: 1,
              padding: '12px 15px',
              border: `1px solid ${connectionStatus === 'connected' ? '#28a745' : connectionStatus === 'connecting' ? '#ffc107' : '#dc3545'}`,
              borderRadius: '20px',
              outline: 'none',
              fontSize: 'var(--font-size-base)',
              background: connectionStatus === 'connecting' ? '#fff3cd' : 'white',
              transition: 'var(--transition)'
            }}
            onKeyPress={(e) => {
              if (e.key === 'Enter' && !e.shiftKey) {
                e.preventDefault();
                handleSendMessage(e);
              }
            }}
          />
          <button
            type="submit"
            disabled={!newMessage.trim() || connectionStatus === 'connecting'}
            style={{
              padding: '12px 20px',
              background: (newMessage.trim() && connectionStatus !== 'connecting') ? 
                         (connectionStatus === 'connected' ? '#28a745' : '#6c757d') : '#ccc',
              color: 'white',
              border: 'none',
              borderRadius: '20px',
              cursor: (newMessage.trim() && connectionStatus !== 'connecting') ? 'pointer' : 'not-allowed',
              fontSize: 'var(--font-size-base)',
              transition: 'var(--transition)',
              fontWeight: '500'
            }}
            onMouseOver={(e) => (newMessage.trim() && connectionStatus !== 'connecting') && 
              (e.target.style.background = connectionStatus === 'connected' ? '#218838' : '#5a6268')}
            onMouseOut={(e) => (newMessage.trim() && connectionStatus !== 'connecting') && 
              (e.target.style.background = connectionStatus === 'connected' ? '#28a745' : '#6c757d')}
          >
            {connectionStatus === 'connecting' ? 'â³' : 
             connectionStatus === 'connected' ? 'ðŸš€' : 'ðŸ’«'}
          </button>
        </div>
        <div style={{
          textAlign: 'center',
          color: statusInfo.color,
          fontSize: 'var(--font-size-sm)',
          marginTop: '8px',
          fontWeight: 'bold'
        }}>
          {statusInfo.message}
          {connectionStatus === 'error' && ' - Utilisation du mode local'}
        </div>
      </form>
    </div>
  );
}

// Composant principal avec salons et chat
function AppContent() {
  const { user, logout } = useAuth();
  const { theme, toggleTheme } = useTheme();
  const [rooms, setRooms] = useState([]);
  const [myRooms, setMyRooms] = useState([]);
  const [showRoomForm, setShowRoomForm] = useState(false);
  const [newRoomName, setNewRoomName] = useState('');
  const [activeTab, setActiveTab] = useState('public');
  const [loading, setLoading] = useState(false);
  const [activeRoom, setActiveRoom] = useState(null);
  const [apiStatus, setApiStatus] = useState('checking');

  // Fonction de secours avec donnÃ©es dÃ©mo
  const loadDemoData = () => {
    console.log(' Utilisation des donnÃ©es de dÃ©mo');
    const demoRooms = [
      {
        _id: 'demo-1',
        name: 'gÃ©nÃ©ral',
        description: 'Salon de discussion gÃ©nÃ©rale',
        createdBy: { _id: 'system', username: 'SystÃ¨me' },
        members: [{ _id: 'system', username: 'SystÃ¨me' }],
        createdAt: new Date()
      }
    ];
    setRooms(demoRooms);
    setMyRooms(demoRooms);
    setApiStatus('demo');
  };

  // Charger les salons - VERSION RÃ‰ELLE
  const loadRooms = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');

      console.log(' Chargement des salons depuis le backend...');

      // Test d'abord la connexion API
      try {
        const healthResponse = await fetch('http://localhost:5000/api/health');
        if (healthResponse.ok) {
          setApiStatus('connected');
        } else {
          setApiStatus('error');
          throw new Error('API health check failed');
        }
      } catch (error) {
        setApiStatus('error');
        console.error(' API non disponible:', error);
        loadDemoData();
        return;
      }

      // Salons publics
      const publicResponse = await fetch('http://localhost:5000/api/chatrooms', {
        headers: { 
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      console.log(' RÃ©ponse salons publics:', publicResponse.status);

      if (publicResponse.ok) {
        const publicData = await publicResponse.json();
        console.log(' DonnÃ©es salons publics:', publicData);

        if (publicData.success) {
          setRooms(publicData.data.chatRooms || []);

          // Mes salons
          const myResponse = await fetch('http://localhost:5000/api/chatrooms/my', {
            headers: { 
              'Authorization': `Bearer ${token}`,
              'Content-Type': 'application/json'
            }
          });

          if (myResponse.ok) {
            const myData = await myResponse.json();
            if (myData.success) {
              setMyRooms(myData.data.chatRooms || []);
              console.log(' Salons chargÃ©s avec succÃ¨s depuis le backend');
              return;
            }
          }
        }
      }

      // Si on arrive ici, l'API a Ã©chouÃ©
      console.error(' Erreur API, utilisation donnÃ©es dÃ©mo');
      loadDemoData();

    } catch (error) {
      console.error(' Erreur chargement salons:', error);
      loadDemoData();
    } finally {
      setLoading(false);
    }
  };

  // CrÃ©er un salon
  const createRoom = async (name) => {
    try {
      const token = localStorage.getItem('token');

      if (apiStatus === 'connected') {
        // Utiliser l'API rÃ©elle
        const response = await fetch('http://localhost:5000/api/chatrooms', {
          method: 'POST',
          headers: { 
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          },
          body: JSON.stringify({ name, description: '' })
        });

        if (response.ok) {
          await loadRooms();
          return;
        }
      }

      // Fallback: crÃ©ation locale
      const newRoom = {
        _id: `demo-${Date.now()}`,
        name,
        description: '',
        createdBy: user,
        members: [user],
        createdAt: new Date()
      };

      setRooms(prev => [...prev, newRoom]);
      setMyRooms(prev => [...prev, newRoom]);

    } catch (error) {
      console.error('Erreur crÃ©ation salon:', error);
    }
  };

  // Rejoindre un salon
  const joinRoom = async (roomId) => {
    try {
      const token = localStorage.getItem('token');

      if (apiStatus === 'connected') {
        const response = await fetch(`http://localhost:5000/api/chatrooms/${roomId}/join`, {
          method: 'POST',
          headers: { 
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });

        if (response.ok) {
          await loadRooms();
          return;
        }
      }

      // Fallback: rejoindre localement
      setRooms(prev => prev.map(room => 
        room._id === roomId 
          ? { ...room, members: [...room.members, user] }
          : room
      ));

      setMyRooms(prev => {
        const roomToJoin = rooms.find(r => r._id === roomId);
        if (roomToJoin && !prev.some(r => r._id === roomId)) {
          return [...prev, { ...roomToJoin, members: [...roomToJoin.members, user] }];
        }
        return prev;
      });

    } catch (error) {
      console.error('Erreur rejoindre salon:', error);
    }
  };

  // Quitter un salon
  const leaveRoom = async (roomId) => {
    try {
      const token = localStorage.getItem('token');

      if (apiStatus === 'connected') {
        const response = await fetch(`http://localhost:5000/api/chatrooms/${roomId}/leave`, {
          method: 'POST',
          headers: { 
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });

        if (response.ok) {
          await loadRooms();
          return;
        }
      }

      // Fallback: quitter localement
      setRooms(prev => prev.map(room => 
        room._id === roomId 
          ? { ...room, members: room.members.filter(member => member._id !== user._id) }
          : room
      ));

      setMyRooms(prev => prev.filter(room => room._id !== roomId));

      if (activeRoom && activeRoom._id === roomId) {
        setActiveRoom(null);
      }

    } catch (error) {
      console.error('Erreur quitter salon:', error);
    }
  };

  // Entrer dans un salon
  const enterRoom = (room) => {
    setActiveRoom(room);
  };

  // Fermer le chat
  const closeChat = () => {
    setActiveRoom(null);
  };

  // Charger les salons au dÃ©marrage
  useEffect(() => {
    if (user) {
      loadRooms();
    }
