/**
 * Contexte d'authentification React
 * G√®re l'√©tat global de l'authentification dans l'application
 * Fournit les fonctions de connexion, inscription et d√©connexion
 */
import React, { createContext, useState, useContext, useEffect, useCallback } from 'react';
import { authAPI } from '../services/api';

// Cr√©ation du contexte d'authentification
const AuthContext = createContext();

/**
 * Hook personnalis√© pour utiliser le contexte d'authentification
 * @returns {Object} Contexte d'authentification avec user, loading, error et fonctions d'auth
 * @throws {Error} Si utilis√© en dehors d'un AuthProvider
 */
export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

/**
 * Provider du contexte d'authentification
 * @param {Object} props - Props du composant
 * @param {ReactNode} props.children - Composants enfants √† envelopper
 */
export const AuthProvider = ({ children }) => {
  // √âtat de l'utilisateur connect√© (null si non connect√©)
  const [user, setUser] = useState(null);
  // √âtat de chargement (true pendant les requ√™tes async)
  const [loading, setLoading] = useState(true);
  // √âtat des erreurs d'authentification
  const [error, setError] = useState('');

  /**
   * D√©connexion de l'utilisateur
   * Nettoie le localStorage et remet √† z√©ro l'√©tat
   */
  const logout = useCallback(async () => {
    console.log('üö™ D√©connexion en cours...');
    try {
      // Appeler l'API de d√©connexion c√¥t√© serveur si un token existe
      const token = localStorage.getItem('token');
      if (token) {
        console.log('üì° Appel API d√©connexion...');
        await authAPI.logout();
      }
    } catch (error) {
      console.error('‚ùå Erreur API d√©connexion:', error);
      // Continuer quand m√™me le nettoyage local en cas d'erreur
    } finally {
      // Nettoyer le localStorage et remettre √† z√©ro l'√©tat
      console.log('üßπ Nettoyage localStorage...');
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      setUser(null);
      setError('');
      setLoading(false);
      console.log('‚úÖ D√©connexion termin√©e');
    }
  }, []); // Pas de d√©pendances

  /**
   * V√©rifie si l'utilisateur est authentifi√© au chargement de l'application
   * Utilise useCallback pour m√©moriser la fonction et √©viter les re-cr√©ations
   */
  const checkAuth = useCallback(async () => {
    try {
      console.log('üîç V√©rification de l\'authentification...');
      
      // R√©cup√®re le token et les donn√©es utilisateur du localStorage
      const token = localStorage.getItem('token');
      const savedUser = localStorage.getItem('user');

      console.log('Token pr√©sent:', !!token);
      console.log('User sauvegard√©:', !!savedUser);

      // Si pas de token, arr√™ter ici
      if (!token) {
        console.log('‚ùå Aucun token trouv√©, utilisateur non connect√©');
        setLoading(false);
        return;
      }

      try {
        console.log('üîÑ V√©rification du token avec le serveur...');
        
        // V√©rifier le token avec le serveur
        const response = await authAPI.verify();
        console.log('‚úÖ R√©ponse serveur:', response.data);
        
        if (response.data.success) {
          // Token valide, mettre √† jour l'√©tat utilisateur
          const userData = response.data.data.user;
          console.log('‚úÖ Token valide, utilisateur:', userData.username);
          setUser(userData);
          
          // Mettre √† jour le localStorage avec les donn√©es fra√Æches du serveur
          localStorage.setItem('user', JSON.stringify(userData));
        } else {
          console.log('‚ùå Token invalide c√¥t√© serveur');
          logout();
        }
      } catch (verifyError) {
        // Erreur de v√©rification (network error, token invalide, etc.)
        console.error('‚ùå Erreur v√©rification token:', verifyError.response?.data || verifyError.message);
        
        if (verifyError.response?.status === 401) {
          console.log('üîí Token expir√© ou invalide, d√©connexion...');
          logout();
        } else {
          // Erreur r√©seau, on utilise les donn√©es sauvegard√©es en fallback
          console.log('üåê Erreur r√©seau, utilisation des donn√©es sauvegard√©es');
          if (savedUser) {
            try {
              const userData = JSON.parse(savedUser);
              setUser(userData);
              console.log('üìã Utilisation des donn√©es sauvegard√©es:', userData.username);
            } catch (parseError) {
              console.error('‚ùå Erreur parsing user data:', parseError);
              logout();
            }
          } else {
            // Pas de donn√©es sauvegard√©es, d√©connecter
            logout();
          }
        }
      }
    } catch (error) {
      console.error('üí• Erreur g√©n√©rale checkAuth:', error);
      logout();
    } finally {
      console.log('üèÅ Fin de la v√©rification d\'authentification');
      setLoading(false);
    }
  }, [logout]); // ‚úÖ CORRECTION : logout est maintenant dans les d√©pendances

  // V√©rifier l'authentification au chargement du composant
  useEffect(() => {
    checkAuth();
  }, [checkAuth]); // ‚úÖ checkAuth est dans les d√©pendances

  /**
   * Inscription d'un nouvel utilisateur
   * @param {Object} userData - Donn√©es du nouvel utilisateur {username, email, password}
   * @returns {Object} R√©sultat de l'op√©ration {success: boolean, message?: string, data?: any}
   */
  const register = async (userData) => {
    try {
      setError(''); // R√©initialiser les erreurs
      setLoading(true); // Activer le loading

      const response = await authAPI.register(userData);
      
      if (response.data.success) {
        const { token, user } = response.data.data;
        
        // Sauvegarder les informations dans le localStorage
        localStorage.setItem('token', token);
        localStorage.setItem('user', JSON.stringify(user));
        setUser(user); // Mettre √† jour l'√©tat utilisateur
        
        return { success: true, data: response.data };
      }
    } catch (error) {
      // Gestion des erreurs d'API
      const errorMessage = error.response?.data?.message || 'Erreur lors de l\'inscription';
      setError(errorMessage);
      return { success: false, message: errorMessage };
    } finally {
      setLoading(false); // D√©sactiver le loading
    }
  };

  /**
   * Connexion d'un utilisateur existant
   * @param {Object} credentials - Identifiants de connexion {email, password}
   * @returns {Object} R√©sultat de l'op√©ration {success: boolean, message?: string, data?: any}
   */
  const login = async (credentials) => {
    try {
      setError(''); // R√©initialiser les erreurs
      setLoading(true); // Activer le loading

      const response = await authAPI.login(credentials);
      
      if (response.data.success) {
        const { token, user } = response.data.data;
        
        // Sauvegarder les informations dans le localStorage
        localStorage.setItem('token', token);
        localStorage.setItem('user', JSON.stringify(user));
        setUser(user); // Mettre √† jour l'√©tat utilisateur
        
        return { success: true, data: response.data };
      }
    } catch (error) {
      // Gestion des erreurs d'API
      const errorMessage = error.response?.data?.message || 'Erreur lors de la connexion';
      setError(errorMessage);
      return { success: false, message: errorMessage };
    } finally {
      setLoading(false); // D√©sactiver le loading
    }
  };

  /**
   * Effacer les messages d'erreur
   */
  const clearError = () => {
    setError('');
  };

  // Valeur du contexte √† fournir aux composants enfants
  const value = {
    user,           // Utilisateur connect√© ou null
    loading,        // √âtat de chargement
    error,          // Message d'erreur
    register,       // Fonction d'inscription
    login,          // Fonction de connexion
    logout,         // Fonction de d√©connexion
    clearError,     // Fonction pour effacer les erreurs
    checkAuth,      // Fonction pour v√©rifier l'authentification
    isAuthenticated: !!user // Bool√©en indiquant si l'utilisateur est connect√©
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
