import React, { useState, useEffect } from 'react';
import { AuthProvider, useAuth } from './contexts/AuthContext';
import { ChatRoomProvider, useChatRoom } from './contexts/ChatRoomContext';
import CreateRoomForm from './components/CreateRoomForm';
import RoomList from './components/RoomList';
import './styles/chatRooms.css';

// Composant Modal séparé pour éviter les erreurs DOM
const Modal = ({ children, onClose }) => {
  useEffect(() => {
    const handleEscape = (e) => {
      if (e.key === 'Escape') onClose();
    };

    document.addEventListener('keydown', handleEscape);
    document.body.style.overflow = 'hidden';

    return () => {
      document.removeEventListener('keydown', handleEscape);
      document.body.style.overflow = 'unset';
    };
  }, [onClose]);

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        {children}
      </div>
    </div>
  );
};

// Composant principal de l'application
function AppContent() {
  const { user, login, register, logout, error: authError, clearError: clearAuthError } = useAuth();
  const { error: chatError, clearError: clearChatError } = useChatRoom();
  const [showCreateRoom, setShowCreateRoom] = useState(false);
  const [authMode, setAuthMode] = useState('login');
  const [authData, setAuthData] = useState({
    username: '',
    email: '',
    password: ''
  });

  // Fusion des erreurs
  const error = authError || chatError;
  const clearError = () => {
    clearAuthError();
    clearChatError();
  };

  const handleAuthSubmit = async (e) => {
    e.preventDefault();
    if (authMode === 'login') {
      await login(authData.email, authData.password);
    } else {
      await register(authData.username, authData.email, authData.password);
    }
  };

  const handleCloseModal = () => {
    setShowCreateRoom(false);
  };

  if (!user) {
    return (
      <div className="auth-container">
        <div className="auth-form">
          <h2>{authMode === 'login' ? 'Connexion' : 'Inscription'}</h2>

          {error && (
            <div className="error-message">
              {error}
              <button type="button" onClick={clearError}>×</button>
            </div>
          )}

          <form onSubmit={handleAuthSubmit}>
            {authMode === 'register' && (
              <div className="form-group">
                <input
                  type="text"
                  placeholder="Nom d'utilisateur"
                  value={authData.username}
                  onChange={(e) => setAuthData({...authData, username: e.target.value})}
                  required
                />
              </div>
            )}
            <div className="form-group">
              <input
                type="email"
                placeholder="Email"
                value={authData.email}
                onChange={(e) => setAuthData({...authData, email: e.target.value})}
                required
              />
            </div>
            <div className="form-group">
              <input
                type="password"
                placeholder="Mot de passe"
                value={authData.password}
                onChange={(e) => setAuthData({...authData, password: e.target.value})}
                required
              />
            </div>
            <button type="submit" className="btn-primary">
              {authMode === 'login' ? 'Se connecter' : 'S\'inscrire'}
            </button>
          </form>

          <button 
            className="switch-mode"
            onClick={() => {
              setAuthMode(authMode === 'login' ? 'register' : 'login');
              clearError();
            }}
            type="button"
          >
            {authMode === 'login' 
              ? 'Pas de compte ? S\'inscrire' 
              : 'Déjà un compte ? Se connecter'
            }
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="app">
      <header className="app-header">
        <h1>Chat Application</h1>
        <div className="user-info">
          <span>Bonjour, {user.username}!</span>
          <button onClick={logout} className="btn-secondary">Déconnexion</button>
        </div>
      </header>

      <main className="main-content">
        <div className="content-header">
          <h2>Salons de Discussion</h2>
          <button 
            className="btn-primary"
            onClick={() => setShowCreateRoom(true)}
            type="button"
          >
            + Créer un Salon
          </button>
        </div>

        {error && (
          <div className="error-message">
            {error}
            <button type="button" onClick={clearError}>×</button>
          </div>
        )}

        {/* Modal utilisant le composant séparé */}
        {showCreateRoom && (
          <Modal onClose={handleCloseModal}>
            <CreateRoomForm onClose={handleCloseModal} />
          </Modal>
        )}

        <RoomList />
      </main>
    </div>
  );
}

// Composant App principal
function App() {
  return (
    <AuthProvider>
      <ChatRoomProvider>
        <AppContent />
      </ChatRoomProvider>
    </AuthProvider>
  );
}

export default App;
