import React, { useState, useEffect } from 'react';

// Contexte d'authentification
const AuthContext = React.createContext();

export const useAuth = () => {
  const context = React.useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  const login = async (email, password) => {
    try {
      const response = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });
      const data = await response.json();
      if (data.success) {
        localStorage.setItem('token', data.data.token);
        setUser(data.data.user);
        return { success: true };
      }
      return { success: false, message: data.message };
    } catch (error) {
      return { success: false, message: 'Erreur de connexion' };
    }
  };

  const register = async (username, email, password) => {
    try {
      const response = await fetch('http://localhost:5000/api/auth/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, email, password }),
      });
      const data = await response.json();
      if (data.success) {
        localStorage.setItem('token', data.data.token);
        setUser(data.data.user);
        return { success: true };
      }
      return { success: false, message: data.message };
    } catch (error) {
      return { success: false, message: 'Erreur d\'inscription' };
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    setUser(null);
  };

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      fetch('http://localhost:5000/api/auth/profile', {
        headers: { 'Authorization': `Bearer ${token}` }
      })
      .then(r => r.json())
      .then(data => {
        if (data.success) setUser(data.data.user);
      })
      .catch(() => localStorage.removeItem('token'))
      .finally(() => setLoading(false));
    } else {
      setLoading(false);
    }
  }, []);

  if (loading) {
    return (
      <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '100vh',
        background: '#f5f5f5'
      }}>
        <div>Chargement...</div>
      </div>
    );
  }

  return (
    <AuthContext.Provider value={{ user, login, register, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

// Composant de connexion
function LoginForm() {
  const { login, register } = useAuth();
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({ username: '', email: '', password: '' });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    const result = isLogin 
      ? await login(formData.email, formData.password)
      : await register(formData.username, formData.email, formData.password);

    setLoading(false);

    if (!result.success) {
      setError(result.message);
    }
  };

  return (
    <div style={{
      display: 'flex', 
      justifyContent: 'center', 
      alignItems: 'center', 
      minHeight: '100vh', 
      background: 'linear-gradient(135deg, #667eea, #764ba2)'
    }}>
      <div style={{
        background: 'white', 
        padding: '30px', 
        borderRadius: '10px', 
        boxShadow: '0 4px 20px rgba(0,0,0,0.1)', 
        width: '100%', 
        maxWidth: '400px'
      }}>
        <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>
          {isLogin ? 'Connexion' : 'Inscription'}
        </h2>

        {error && (
          <div style={{
            background: '#f8d7da', 
            color: '#721c24', 
            padding: '12px',
            borderRadius: '5px', 
            marginBottom: '15px',
            fontSize: '14px'
          }}>
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          {!isLogin && (
            <input
              type="text"
              placeholder="Nom d'utilisateur"
              value={formData.username}
              onChange={(e) => setFormData({...formData, username: e.target.value})}
              required
              style={{
                width: '100%', 
                padding: '12px', 
                marginBottom: '15px',
                border: '1px solid #ddd', 
                borderRadius: '5px',
                fontSize: '16px'
              }}
            />
          )}
          <input
            type="email"
            placeholder="Email"
            value={formData.email}
            onChange={(e) => setFormData({...formData, email: e.target.value})}
            required
            style={{
              width: '100%', 
              padding: '12px', 
              marginBottom: '15px',
              border: '1px solid #ddd', 
              borderRadius: '5px',
              fontSize: '16px'
            }}
          />
          <input
            type="password"
            placeholder="Mot de passe"
            value={formData.password}
            onChange={(e) => setFormData({...formData, password: e.target.value})}
            required
            style={{
              width: '100%', 
              padding: '12px', 
              marginBottom: '15px',
              border: '1px solid #ddd', 
              borderRadius: '5px',
              fontSize: '16px'
            }}
          />
          <button 
            type="submit" 
            disabled={loading}
            style={{
              width: '100%', 
              padding: '12px', 
              background: loading ? '#6c757d' : '#007bff',
              color: 'white', 
              border: 'none', 
              borderRadius: '5px', 
              cursor: loading ? 'not-allowed' : 'pointer',
              fontSize: '16px'
            }}
          >
            {loading ? 'Chargement...' : (isLogin ? 'Se connecter' : 'S\'inscrire')}
          </button>
        </form>
        <button 
          onClick={() => { setIsLogin(!isLogin); setError(''); }} 
          style={{
            background: 'none', 
            border: 'none', 
            color: '#007bff',
            cursor: 'pointer', 
            marginTop: '15px', 
            width: '100%',
            fontSize: '14px'
          }}
        >
          {isLogin ? 'Pas de compte ? S\'inscrire' : 'DÃ©jÃ  un compte ? Se connecter'}
        </button>
      </div>
    </div>
  );
}

// Composant de Chat
function ChatRoom({ room, onClose }) {
  const { user } = useAuth();
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  // eslint-disable-next-line react-hooks/exhaustive-deps
  useEffect(() => {
    // Simulation d'historique de messages
    setMessages([
      {
        _id: 1,
        content: `Bienvenue dans le salon ${room.name} ! ðŸŽ‰`,
        user: { _id: 'system', username: 'SystÃ¨me' },
        createdAt: new Date(),
        isSystem: true
      },
      {
        _id: 2,
        content: 'Ceci est une dÃ©monstration du systÃ¨me de chat.',
        user: { _id: 'system', username: 'SystÃ¨me' },
        createdAt: new Date(),
        isSystem: true
      },
      {
        _id: 3,
        content: 'Les messages sont stockÃ©s localement pour le moment.',
        user: { _id: 'system', username: 'SystÃ¨me' },
        createdAt: new Date(),
        isSystem: true
      },
      {
        _id: 4,
        content: 'Testez en envoyant un message !',
        user: { _id: 'system', username: 'SystÃ¨me' },
        createdAt: new Date(),
        isSystem: true
      }
    ]);
  }, [room]);

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (!newMessage.trim()) return;

    // Ajouter le message localement
    const message = {
      _id: Date.now(),
      content: newMessage.trim(),
      user: user,
      createdAt: new Date()
    };

    setMessages(prev => [...prev, message]);
    setNewMessage('');

    // Simulation de rÃ©ponse automatique aprÃ¨s 1 seconde
    setTimeout(() => {
      const autoReply = {
        _id: Date.now() + 1,
        content: `Merci pour votre message dans #${room.name} ! (RÃ©ponse automatique)`,
        user: { _id: 'bot', username: 'Bot' },
        createdAt: new Date()
      };
      setMessages(prev => [...prev, autoReply]);
    }, 1000);
  };

  const formatTime = (date) => {
    if (typeof date === 'string') {
      date = new Date(date);
    }
    return date.toLocaleTimeString('fr-FR', { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  const formatDate = (date) => {
    if (typeof date === 'string') {
      date = new Date(date);
    }
    return date.toLocaleDateString('fr-FR');
  };

  // Grouper les messages par date
  const groupMessagesByDate = () => {
    const groups = {};
    messages.forEach(message => {
      const date = formatDate(message.createdAt);
      if (!groups[date]) {
        groups[date] = [];
      }
      groups[date].push(message);
    });
    return groups;
  };

  const messageGroups = groupMessagesByDate();

  return (
    <div style={{
      display: 'flex',
      flexDirection: 'column',
      height: '600px',
      border: '1px solid #e9ecef',
      borderRadius: '8px',
      background: 'white',
      marginLeft: '20px',
      flex: 2,
      minWidth: '500px'
    }}>
      {/* En-tÃªte du chat */}
      <div style={{
        padding: '15px 20px',
        borderBottom: '1px solid #e9ecef',
        background: '#f8f9fa',
        borderRadius: '8px 8px 0 0',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center'
      }}>
        <div>
          <h3 style={{ margin: 0, color: '#333' }}>#{room.name}</h3>
          <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
            <small style={{ color: '#6c757d' }}>
              {room.members.length} membre(s)
            </small>
            <div style={{
              width: '8px',
              height: '8px',
              borderRadius: '50%',
              background: '#28a745'
            }} />
            <small style={{ color: '#28a745' }}>
              En ligne (DÃ©mo)
            </small>
          </div>
        </div>
        <button 
          onClick={onClose}
          style={{
            background: '#6c757d',
            color: 'white',
            border: 'none',
            padding: '8px 12px',
            borderRadius: '5px',
            cursor: 'pointer'
          }}
        >
          âœ• Fermer
        </button>
      </div>

      {/* Zone des messages */}
      <div style={{
        flex: 1,
        overflowY: 'auto',
        padding: '20px',
        background: '#fafafa'
      }}>
        {messages.length === 0 ? (
          <div style={{
            textAlign: 'center',
            color: '#666',
            fontStyle: 'italic',
            marginTop: '50px'
          }}>
            <div style={{ fontSize: '48px', marginBottom: '10px' }}>ðŸ’¬</div>
            <div>Aucun message dans ce salon.</div>
            <div>Soyez le premier Ã  envoyer un message !</div>
          </div>
        ) : (
          Object.entries(messageGroups).map(([date, dayMessages]) => (
            <div key={date}>
              {/* SÃ©parateur de date */}
              <div style={{
                textAlign: 'center',
                margin: '20px 0',
                position: 'relative'
              }}>
                <div style={{
                  display: 'inline-block',
                  background: '#e9ecef',
                  color: '#6c757d',
                  padding: '5px 15px',
                  borderRadius: '15px',
                  fontSize: '12px',
                  fontWeight: 'bold'
                }}>
                  {date}
                </div>
              </div>

              {/* Messages du jour */}
              {dayMessages.map((message) => (
                <div
                  key={message._id}
                  style={{
                    marginBottom: '15px',
                    padding: message.isSystem ? '10px 15px' : '12px 16px',
                    borderRadius: message.isSystem ? '5px' : '12px',
                    background: message.isSystem ? '#e9ecef' : 
                               message.user._id === user._id ? '#007bff' : 'white',
                    color: message.isSystem ? '#6c757d' : 
                          message.user._id === user._id ? 'white' : '#333',
                    border: message.isSystem ? 'none' : 
                           message.user._id === user._id ? 'none' : '1px solid #e9ecef',
                    maxWidth: message.isSystem ? '100%' : '75%',
                    alignSelf: message.user._id === user._id ? 'flex-end' : 'flex-start',
                    marginLeft: message.user._id === user._id ? 'auto' : '0',
                    marginRight: message.user._id === user._id ? '0' : 'auto',
                    fontStyle: message.isSystem ? 'italic' : 'normal'
                  }}
                >
                  {!message.isSystem && (
                    <div style={{
                      display: 'flex',
                      justifyContent: 'space-between',
                      alignItems: 'flex-start',
                      marginBottom: '5px'
                    }}>
                      <strong style={{
                        fontSize: '14px',
                        color: message.user._id === user._id ? 'white' : '#007bff'
                      }}>
                        {message.user._id === user._id ? 'Vous' : message.user.username}
                      </strong>
                      <small style={{
                        opacity: 0.8,
                        fontSize: '11px',
                        marginLeft: '10px'
                      }}>
                        {formatTime(message.createdAt)}
                      </small>
                    </div>
                  )}
                  <div style={{ 
                    lineHeight: '1.4',
                    fontSize: message.isSystem ? '13px' : '14px'
                  }}>
                    {message.content}
                  </div>
                </div>
              ))}
            </div>
          ))
        )}
      </div>

      {/* Zone de saisie */}
      <form onSubmit={handleSendMessage} style={{
        padding: '20px',
        borderTop: '1px solid #e9ecef',
        background: 'white',
        borderRadius: '0 0 8px 8px'
      }}>
        <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
          <input
            type="text"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder="Tapez votre message..."
            style={{
              flex: 1,
              padding: '12px 15px',
              border: '1px solid #ddd',
              borderRadius: '20px',
              outline: 'none',
              fontSize: '14px'
            }}
            onKeyPress={(e) => {
              if (e.key === 'Enter' && !e.shiftKey) {
                e.preventDefault();
                handleSendMessage(e);
              }
            }}
          />
          <button
            type="submit"
            disabled={!newMessage.trim()}
            style={{
              padding: '12px 20px',
              background: newMessage.trim() ? '#007bff' : '#ccc',
              color: 'white',
              border: 'none',
              borderRadius: '20px',
              cursor: newMessage.trim() ? 'pointer' : 'not-allowed',
              fontSize: '14px'
            }}
          >
            ðŸ“¤
          </button>
        </div>
        <div style={{
          textAlign: 'center',
          color: '#28a745',
          fontSize: '12px',
          marginTop: '8px'
        }}>
          âœ“ Mode dÃ©monstration - Messages locaux
        </div>
      </form>
    </div>
  );
}

// Composant principal avec salons et chat
function AppContent() {
  const { user, logout } = useAuth();
  const [rooms, setRooms] = useState([]);
  const [myRooms, setMyRooms] = useState([]);
  const [showRoomForm, setShowRoomForm] = useState(false);
  const [newRoomName, setNewRoomName] = useState('');
  const [activeTab, setActiveTab] = useState('public');
  const [loading, setLoading] = useState(false);
  const [activeRoom, setActiveRoom] = useState(null);

  // Charger les salons
  const loadRooms = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');

      // Salons publics
      const publicResponse = await fetch('http://localhost:5000/api/chatrooms', {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      const publicData = await publicResponse.json();
      if (publicData.success) setRooms(publicData.data.chatRooms || []);

      // Mes salons
      const myResponse = await fetch('http://localhost:5000/api/chatrooms/my', {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      const myData = await myResponse.json();
      if (myData.success) setMyRooms(myData.data.chatRooms || []);

    } catch (error) {
      console.error('Erreur chargement salons:', error);
    } finally {
      setLoading(false);
    }
  };

  // CrÃ©er un salon
  const createRoom = async (name) => {
    try {
      const token = localStorage.getItem('token');
      await fetch('http://localhost:5000/api/chatrooms', {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ name, description: '' })
      });

      // Recharger les salons
      await loadRooms();
    } catch (error) {
      console.error('Erreur crÃ©ation salon:', error);
    }
  };

  // Rejoindre un salon
  const joinRoom = async (roomId) => {
    try {
      const token = localStorage.getItem('token');
      await fetch(`http://localhost:5000/api/chatrooms/${roomId}/join`, {
        method: 'POST',
        headers: { 'Authorization': `Bearer ${token}` }
      });

      // Recharger les salons
      await loadRooms();
    } catch (error) {
      console.error('Erreur rejoindre salon:', error);
    }
  };

  // Quitter un salon
  const leaveRoom = async (roomId) => {
    try {
      const token = localStorage.getItem('token');
      await fetch(`http://localhost:5000/api/chatrooms/${roomId}/leave`, {
        method: 'POST',
        headers: { 'Authorization': `Bearer ${token}` }
      });

      // Si on quitte le salon actif, fermer le chat
      if (activeRoom && activeRoom._id === roomId) {
        setActiveRoom(null);
      }

      // Recharger les salons
      await loadRooms();
    } catch (error) {
      console.error('Erreur quitter salon:', error);
    }
  };

  // Entrer dans un salon (ouvrir le chat)
  const enterRoom = (room) => {
    setActiveRoom(room);
  };

  // Fermer le chat
  const closeChat = () => {
    setActiveRoom(null);
  };

  // Charger les salons au dÃ©marrage
  useEffect(() => {
    if (user) {
      loadRooms();
    }
  }, [user]);

  if (!user) {
    return <LoginForm />;
  }

  const displayRooms = activeTab === 'public' ? rooms : myRooms;

  return (
    <div style={{ minHeight: '100vh', background: '#f5f5f5' }}>
      <header style={{
        background: '#007bff', 
        color: 'white', 
        padding: '15px 20px',
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center'
      }}>
        <h1 style={{ margin: 0, fontSize: '24px' }}>Application de chat</h1>
        <div style={{ display: 'flex', alignItems: 'center', gap: '15px' }}>
          <span>Bonjour, {user.username} !</span>
          <button 
            onClick={logout}
            style={{
              background: '#6c757d', 
              color: 'white', 
              border: 'none',
              padding: '8px 16px', 
              borderRadius: '5px', 
              cursor: 'pointer'
            }}
          >
            DÃ©connexion
          </button>
        </div>
      </header>

      <main style={{ padding: '20px', maxWidth: '1400px', margin: '0 auto' }}>
        <div style={{
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'center',
          marginBottom: '20px'
        }}>
          <h2 style={{ margin: 0 }}>Salons de discussion</h2>
          <button 
            onClick={() => setShowRoomForm(true)}
            style={{
              background: '#007bff', 
              color: 'white', 
              border: 'none',
              padding: '10px 20px', 
              borderRadius: '5px', 
              cursor: 'pointer'
            }}
          >
            + CrÃ©er un salon
          </button>
        </div>

        {/* Modal crÃ©ation salon */}
        {showRoomForm && (
          <div style={{
            position: 'fixed', 
            top: 0, 
            left: 0, 
            right: 0, 
            bottom: 0, 
            background: 'rgba(0,0,0,0.5)', 
            display: 'flex', 
            justifyContent: 'center', 
            alignItems: 'center', 
            zIndex: 1000
          }}>
            <div style={{
              background: 'white', 
              padding: '25px', 
              borderRadius: '10px', 
              width: '90%', 
              maxWidth: '400px'
            }}>
              <h3 style={{ marginBottom: '20px', textAlign: 'center' }}>
                CrÃ©er un nouveau salon
              </h3>
              <input
                type="text"
                placeholder="Nom du salon"
                value={newRoomName}
                onChange={(e) => setNewRoomName(e.target.value)}
                style={{
                  width: '100%', 
                  padding: '12px', 
                  marginBottom: '20px',
                  border: '2px solid #ddd', 
                  borderRadius: '5px', 
                  fontSize: '16px'
                }}
                autoFocus
              />
              <div style={{ display: 'flex', gap: '10px', justifyContent: 'flex-end' }}>
                <button 
                  onClick={() => { setShowRoomForm(false); setNewRoomName(''); }}
                  style={{
                    background: '#6c757d', 
                    color: 'white', 
                    border: 'none',
                    padding: '10px 20px', 
                    borderRadius: '5px', 
                    cursor: 'pointer'
                  }}
                >
                  Annuler
                </button>
                <button 
                  onClick={() => { 
                    if (newRoomName.trim()) {
                      createRoom(newRoomName);
                      setShowRoomForm(false);
                      setNewRoomName('');
                    }
                  }}
                  style={{
                    background: '#007bff', 
                    color: 'white', 
                    border: 'none',
                    padding: '10px 20px', 
                    borderRadius: '5px', 
                    cursor: 'pointer'
                  }}
                >
                  CrÃ©er
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Onglets */}
        <div style={{ display: 'flex', gap: '10px', marginBottom: '20px' }}>
          <button 
            onClick={() => setActiveTab('public')}
            style={{
              padding: '10px 20px', 
              border: 'none', 
              background: activeTab === 'public' ? '#007bff' : 'white',
              color: activeTab === 'public' ? 'white' : '#333', 
              borderRadius: '5px', 
              cursor: 'pointer'
            }}
          >
            Salons Publics ({rooms.length})
          </button>
          <button 
            onClick={() => setActiveTab('my')}
            style={{
              padding: '10px 20px', 
              border: 'none', 
              background: activeTab === 'my' ? '#007bff' : 'white',
              color: activeTab === 'my' ? 'white' : '#333', 
              borderRadius: '5px', 
              cursor: 'pointer'
            }}
          >
            Mes Salons ({myRooms.length})
          </button>
        </div>

        {/* Conteneur principal avec liste et chat */}
        <div style={{ display: 'flex', gap: '20px', alignItems: 'flex-start' }}>
          {/* Liste des salons */}
          <div style={{ 
            flex: activeRoom ? 1 : 1, 
            maxWidth: activeRoom ? '400px' : '100%',
            minWidth: '300px'
          }}>
            <div style={{ background: 'white', padding: '20px', borderRadius: '10px' }}>
              {loading ? (
                <div style={{ textAlign: 'center', padding: '40px', color: '#666' }}>
                  Chargement des salons...
                </div>
              ) : displayRooms.length === 0 ? (
                <div style={{ textAlign: 'center', padding: '40px', color: '#666', fontStyle: 'italic' }}>
                  {activeTab === 'public' ? 'Aucun salon public trouvÃ©' : 'Vous n\'Ãªtes membre d\'aucun salon'}
                </div>
              ) : (
                displayRooms.map(room => (
                  <div 
                    key={room._id} 
                    style={{
                      border: '1px solid #e9ecef', 
                      padding: '15px', 
                      marginBottom: '10px',
                      borderRadius: '8px', 
                      background: '#f8f9fa', 
                      display: 'flex',
                      justifyContent: 'space-between', 
                      alignItems: 'center',
                      borderLeft: activeRoom && activeRoom._id === room._id ? '4px solid #007bff' : '1px solid #e9ecef'
                    }}
                  >
                    <div style={{ flex: 1 }}>
                      <h4 style={{ margin: '0 0 5px 0', color: '#333' }}>#{room.name}</h4>
                      {room.description && (
                        <p style={{ margin: '5px 0', color: '#666', fontSize: '14px' }}>
                          {room.description}
                        </p>
                      )}
                      <div style={{ color: '#6c757d', fontSize: '12px' }}>
                        <span>CrÃ©Ã© par: {room.createdBy.username}</span>
                        <span style={{ marginLeft: '15px' }}>Membres: {room.members.length}</span>
                      </div>
                    </div>

                    <div style={{ display: 'flex', gap: '10px', flexShrink: 0 }}>
                      {room.members.some(member => member._id === user._id) ? (
                        <>
                          <button 
                            onClick={() => enterRoom(room)}
                            style={{
                              background: activeRoom && activeRoom._id === room._id ? '#28a745' : '#007bff',
                              color: 'white', 
                              border: 'none',
                              padding: '8px 16px', 
                              borderRadius: '5px', 
                              cursor: 'pointer',
                              fontSize: '12px'
                            }}
                          >
                            {activeRoom && activeRoom._id === room._id ? 'ðŸ’¬ En chat' : 'ðŸ’¬ Entrer'}
                          </button>
                          {room.createdBy._id !== user._id && (
                            <button 
                              onClick={() => leaveRoom(room._id)}
                              style={{
                                background: '#6c757d', 
                                color: 'white', 
                                border: 'none',
                                padding: '8px 12px', 
                                borderRadius: '5px', 
                                cursor: 'pointer',
                                fontSize: '12px'
                              }}
                            >
                              Quitter
                            </button>
                          )}
                        </>
                      ) : (
                        <button 
                          onClick={() => joinRoom(room._id)}
                          style={{
                            background: '#007bff', 
                            color: 'white', 
                            border: 'none',
                            padding: '8px 16px', 
                            borderRadius: '5px', 
                            cursor: 'pointer',
                            fontSize: '12px'
                          }}
                        >
                          Rejoindre
                        </button>
                      )}
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>

          {/* Zone de chat */}
          {activeRoom && (
            <ChatRoom room={activeRoom} onClose={closeChat} />
          )}
        </div>
      </main>
    </div>
  );
}

// Composant App par dÃ©faut
function App() {
  return (
    <AuthProvider>
      <AppContent />
    </AuthProvider>
  );
}

export default App;
