import React, { useState } from 'react';
import { AuthProvider, useAuth } from './contexts/AuthContext';
import { ChatRoomProvider, useChatRoom } from './contexts/ChatRoomContext';
import './styles/chatRooms.css';

// Composant temporaire pour tester
const SimpleRoomList = () => {
  const { rooms, myRooms, createRoom, joinRoom } = useChatRoom();
  const [showForm, setShowForm] = useState(false);
  const [roomName, setRoomName] = useState('');

  const handleCreateRoom = async (e) => {
    e.preventDefault();
    if (roomName.trim()) {
      await createRoom({ name: roomName, description: '' });
      setRoomName('');
      setShowForm(false);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Salons de Discussion</h2>

      <button 
        onClick={() => setShowForm(true)}
        style={{
          background: '#007bff',
          color: 'white',
          border: 'none',
          padding: '10px 20px',
          borderRadius: '5px',
          cursor: 'pointer',
          marginBottom: '20px'
        }}
      >
        + Créer un Salon
      </button>

      {showForm && (
        <form onSubmit={handleCreateRoom} style={{ marginBottom: '20px' }}>
          <input
            type="text"
            value={roomName}
            onChange={(e) => setRoomName(e.target.value)}
            placeholder="Nom du salon"
            style={{
              padding: '10px',
              marginRight: '10px',
              border: '1px solid #ddd',
              borderRadius: '5px',
              width: '200px'
            }}
          />
          <button type="submit">Créer</button>
          <button type="button" onClick={() => setShowForm(false)}>Annuler</button>
        </form>
      )}

      <h3>Mes Salons ({myRooms.length})</h3>
      {myRooms.length === 0 ? (
        <p>Vous n'êtes membre d'aucun salon</p>
      ) : (
        myRooms.map(room => (
          <div key={room._id} style={{
            border: '1px solid #ddd',
            padding: '15px',
            marginBottom: '10px',
            borderRadius: '5px',
            background: '#f9f9f9'
          }}>
            <h4>{room.name}</h4>
            <p>{room.description}</p>
            <small>Membres: {room.members.length}</small>
          </div>
        ))
      )}

      <h3>Salons Publics ({rooms.length})</h3>
      {rooms.length === 0 ? (
        <p>Aucun salon public</p>
      ) : (
        rooms.map(room => (
          <div key={room._id} style={{
            border: '1px solid #ddd',
            padding: '15px',
            marginBottom: '10px',
            borderRadius: '5px',
            background: '#f9f9f9'
          }}>
            <h4>{room.name}</h4>
            <p>{room.description}</p>
            <small>Créé par: {room.createdBy.username}</small>
            <br/>
            <small>Membres: {room.members.length}</small>
            <br/>
            <button 
              onClick={() => joinRoom(room._id)}
              style={{
                background: '#28a745',
                color: 'white',
                border: 'none',
                padding: '5px 10px',
                borderRadius: '3px',
                cursor: 'pointer',
                marginTop: '10px'
              }}
            >
              Rejoindre
            </button>
          </div>
        ))
      )}
    </div>
  );
};

// Composant principal
function AppContent() {
  const { user, login, register, logout } = useAuth();
  const { error, clearError } = useChatRoom();
  const [authMode, setAuthMode] = useState('login');
  const [authData, setAuthData] = useState({
    username: '',
    email: '',
    password: ''
  });

  const handleAuthSubmit = async (e) => {
    e.preventDefault();
    if (authMode === 'login') {
      await login(authData.email, authData.password);
    } else {
      await register(authData.username, authData.email, authData.password);
    }
  };

  if (!user) {
    return (
      <div className="auth-container">
        <div className="auth-form">
          <h2>{authMode === 'login' ? 'Connexion' : 'Inscription'}</h2>

          {error && (
            <div className="error-message">
              {error}
              <button onClick={clearError}>×</button>
            </div>
          )}

          <form onSubmit={handleAuthSubmit}>
            {authMode === 'register' && (
              <input
                type="text"
                placeholder="Nom d'utilisateur"
                value={authData.username}
                onChange={(e) => setAuthData({...authData, username: e.target.value})}
                required
              />
            )}
            <input
              type="email"
              placeholder="Email"
              value={authData.email}
              onChange={(e) => setAuthData({...authData, email: e.target.value})}
              required
            />
            <input
              type="password"
              placeholder="Mot de passe"
              value={authData.password}
              onChange={(e) => setAuthData({...authData, password: e.target.value})}
              required
            />
            <button type="submit">
              {authMode === 'login' ? 'Se connecter' : 'S\'inscrire'}
            </button>
          </form>

          <button 
            className="switch-mode"
            onClick={() => setAuthMode(authMode === 'login' ? 'register' : 'login')}
          >
            {authMode === 'login' 
              ? 'Pas de compte ? S\'inscrire' 
              : 'Déjà un compte ? Se connecter'
            }
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="app">
      <header className="app-header">
        <h1>Chat Application</h1>
        <div className="user-info">
          <span>Bonjour, {user.username}!</span>
          <button onClick={logout}>Déconnexion</button>
        </div>
      </header>

      <main className="main-content">
        <SimpleRoomList />
      </main>
    </div>
  );
}

function App() {
  return (
    <AuthProvider>
      <ChatRoomProvider>
        <AppContent />
      </ChatRoomProvider>
    </AuthProvider>
  );
}

export default App;
